import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './css/Players.css';

const PAGE_SIZE = 30;
const debounce = (fn, delay) => {
  let timer;
  return (...args) => {
    clearTimeout(timer);
    timer = setTimeout(() => fn(...args), delay);
  };
};

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [offset, setOffset] = useState(0);
  const [search, setSearch] = useState('');
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedBatStyle, setSelectedBatStyle] = useState('');
  const [selectedPosition, setSelectedPosition] = useState('');
  const [countryMap, setCountryMap] = useState({});
  const [batStyles, setBatStyles] = useState([]);
  const [positions, setPositions] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchPlayers();
  }, [offset]);

  useEffect(() => {
    applyFilters();
  }, [search, selectedCountry, selectedBatStyle, selectedPosition, players]);

  const fetchPlayers = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`/api/players?offset=${offset}`);
      const list = res.data.data || [];

      // Build country map
      const newCountries = [...new Set(list.map(p => p.country_id))];
      const updatedCountries = { ...countryMap };
      for (const id of newCountries) {
        if (!updatedCountries[id]) {
          const cRes = await axios.get(`/api/countries/${id}`);
          updatedCountries[id] = cRes.data.data.name;
        }
      }
      setCountryMap(updatedCountries);

      // Extract unique batting styles and positions
      setBatStyles(prev => {
        const setUnique = new Set([...prev, ...list.map(p => p.battingstyle).filter(Boolean)]);
        return Array.from(setUnique);
      });
      setPositions(prev => {
        const setUnique = new Set([...prev, ...list.map(p => p.position?.name).filter(Boolean)]);
        return Array.from(setUnique);
      });

      setPlayers(list);
      setFilteredPlayers(list);
    } catch (err) {
      console.error('Failed to fetch players:', err);
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = debounce(() => {
    const term = search.toLowerCase();
    const filtered = players.filter(p => {
      const fullName = `${p.firstname} ${p.lastname}`.toLowerCase();
      const countryMatch = selectedCountry ? p.country_id === parseInt(selectedCountry) : true;
      const batMatch = selectedBatStyle ? p.battingstyle === selectedBatStyle : true;
      const posMatch = selectedPosition ? p.position?.name === selectedPosition : true;
      return fullName.includes(term) && countryMatch && batMatch && posMatch;
    });
    setFilteredPlayers(filtered);
  }, 300);

let finalPlayers = filteredPlayers;
if (sort === 'name_asc') {
  finalPlayers = finalPlayers.sort((a, b) => a.fullname.localeCompare(b.fullname));
} else if (sort === 'name_desc') {
  finalPlayers = finalPlayers.sort((a, b) => b.fullname.localeCompare(a.fullname));
}

  return (
    <div className="players-list-container">
      <h2>Players</h2>
      <div className="filters">
        <input
          type="text"
          placeholder="Search by name..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <select value={selectedCountry} onChange={e => setSelectedCountry(e.target.value)}>
          <option value="">All Countries</option>
          {Object.entries(countryMap).map(([id, name]) => (
            <option key={id} value={id}>{name}</option>
          ))}
        </select>
        <select value={selectedBatStyle} onChange={e => setSelectedBatStyle(e.target.value)}>
          <option value="">All Batting Styles</option>
          {batStyles.map(bs => (
            <option key={bs} value={bs}>{bs}</option>
          ))}
        </select>
        <select value={selectedPosition} onChange={e => setSelectedPosition(e.target.value)}>
          <option value="">All Positions</option>
          {positions.map(pos => (
            <option key={pos} value={pos}>{pos}</option>
          ))}
        </select>
<select value={sort} onChange={(e) => setSort(e.target.value)}>
  <option value="">Sort by</option>
  <option value="name_asc">Name ↑</option>
  <option value="name_desc">Name ↓</option>
</select>

      </div>

      {loading
        ? <p>Loading...</p>
        : (
          <>
            <div className="players-grid">
              {filteredPlayers.map(player => (
                <div key={player.id} className="player-card">
                  <img src={player.image_path} alt={player.fullname} />
                  <div className="player-info">
                    <h4>{player.fullname}</h4>
                    <p>{countryMap[player.country_id] || 'Unknown'}</p>
                  </div>
                </div>
              ))}
            </div>
            <div className="pagination">
              <button onClick={() => setOffset(prev => Math.max(prev - PAGE_SIZE, 0))} disabled={offset === 0}>
                Previous
              </button>
              <button onClick={() => setOffset(prev => prev + PAGE_SIZE)}>
                Next
              </button>
            </div>
          </>
        )}
    </div>
  );
}
