import React, { useEffect, useState } from 'react';
import './SeriesSquad.css';

const SeriesSquad = () => {
  const seriesId = window.location.pathname.split('/')[2];
  const [squads, setSquads] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!seriesId) return;

    const fetchSquads = async () => {
      try {
        const res = await fetch(`/api/series/${seriesId}/squads`);
        const json = await res.json();
        setSquads(json?.data || []);
      } catch (err) {
        console.error('Error loading series squads', err);
        setSquads([]);
      } finally {
        setLoading(false);
      }
    };

    fetchSquads();
  }, [seriesId]);

  console.log(squads.length);

  return (
    <div className="series-squad">
      <h3>ðŸ§¢ Squads</h3>
      {loading ? (
        <p>Loading squads...</p>
      ) : squads.length === 0 ? (
        <p>No squad data available.</p>
      ) : (
squads.map((team) => (
  <div key={team.teamName} className="squad-team">
    <div className="team-header">
      <img src={team.img} alt={team.teamName} />
      <h4>{team.teamName}</h4>
    </div>
    <ul>
      {(team.Players || []).map((player) => (
        <li key={player.id}>
          {player.name}
          {player.role ? ` â€“ ${player.role}` : ''}
        </li>
      ))}
    </ul>
  </div>
))
