// src/components/OversSummaryTab.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function OversSummaryTab({ matchId }) {
  const [balls, setBalls] = useState([]);
  const [innings, setInnings] = useState({});

  useEffect(() => {
    axios.get(`/api/match/${matchId}`).then((res) => {
      const allBalls = res.data.data.balls || [];

      // Group by scoreboard (inning) → over number
      const grouped = {};

      allBalls.forEach(ball => {
        const inning = ball.scoreboard || 'Inning';
        const over = Math.floor(ball.ball) + 1;

        if (!grouped[inning]) grouped[inning] = {};
        if (!grouped[inning][over]) grouped[inning][over] = [];

        grouped[inning][over].push(ball);
      });

      setBalls(allBalls);
      setInnings(grouped);
    });
  }, [matchId]);

  const getBallDisplay = (ball) => {
    const name = ball?.score?.name || '';
    const runs = ball?.score?.runs ?? '';
    const dismissalsToShowAsW = [
      "Clean Bowled", "LBW OUT", "Catch Out", "Catch Out (Sub)",
      "Stump Out", "Run Out", "Run Out + 1", "Run Out + 2",
      "Run Out (Subs)", "Retired Out", "Hit Wicket", "Absent",
      "Absent Hurt", "Obstructing the field Out"
    ];

    if (dismissalsToShowAsW.includes(name)) return 'W';
    if (name === "Retired Hurt") return 'RH';
    if (runs === 0) return '0';
    if (runs === 4) return '4';
    if (runs === 6) return '6';
    return runs.toString();
  };
console.log(innings);
  return (
    <div className="overs-summary-tab">
      <h3>Overs Summary</h3>

      {Object.entries(innings).map(([inningName, overs]) => (
        <div key={inningName}>
          <h4>{inningName}</h4>
          {Object.keys(overs)
            .sort((a, b) => b - a) // Show latest over first
            .map(over => {
              const ballsInOver = overs[over];

              return (
                <div key={over} style={{ marginBottom: '10px' }}>
                  <strong>Over {over}:</strong>{' '}
                  {ballsInOver.map(ball => (
                    <span key={ball.id} style={{ marginRight: 6 }}>
                      {getBallDisplay(ball)}
                    </span>
                  ))}
                </div>
              );
            })}
        </div>
      ))}
    </div>
  );
}
