import React, { useEffect, useState } from 'react';
import './css/RankingsWidget.css';

const RankingsWidget = () => {
  const [rankings, setRankings] = useState({ odi: [], t20: [], test: [] });
  const [activeTab, setActiveTab] = useState('odi');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRankings = async () => {
      try {
        const res = await fetch(`/api/rankings`);
        const json = await res.json();
        setRankings({
          odi: (json.odi || []).slice(0, 5),
          t20: (json.t20 || []).slice(0, 5),
          test: (json.test || []).slice(0, 5),
        });
      } catch (err) {
        console.error('Error fetching rankings:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchRankings();
  }, []);

  const renderTable = () => (
    <table className="rankings-table">
      <thead>
        <tr>
          <th>Rank</th>
          <th>Team</th>
          <th>Rating</th>
          <th>Points</th>
        </tr>
      </thead>
      <tbody>
        {rankings[activeTab].map((team) => (
          <tr key={team.rank}>
            <td>{team.rank}</td>
            <td>{team.team}</td>
            <td>{team.rating}</td>
            <td>{team.points}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );

  if (loading) return <p>Loading Rankings...</p>;

  return (
    <div className="rankings-widget">
      <h2>üèÜ ICC Team Rankings</h2>
      <div className="tabs">
        {['odi', 't20', 'test'].map((tab) => (
          <button
            key={tab}
            className={activeTab === tab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
          >
            {tab.toUpperCase()}
          </button>
        ))}
      </div>
      {renderTable()}
    </div>
  );
};

export default RankingsWidget;
