import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './css/TeamMatches.css';

const TeamMatches = () => {
  const { teamId } = useParams();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  const LIVE_STATUSES = [
    '1st Innings', '2nd Innings', '3rd Innings', '4th Innings',
    'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4',
    'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.', 'Delayed',
  ];
  const UPCOMING_STATUSES = ['NS', 'Postp.'];
  const COMPLETED_STATUSES = ['Finished', 'Cancl', 'Aban.'];

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch(`/api/team-matches/${teamId}`);
        const json = await res.json();
        setMatches(json.data || []);
      } catch (err) {
        console.error('Error loading team matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [teamId]);

  const filterByStatus = (statuses) =>
    matches.fixtures.filter(m => statuses.includes(m.status));

console.log(matches);

  const renderMatchCard = (match) => (
    <div key={match.id} className="match-card">
      <h4>{match.localteam?.name} vs {match.visitorteam?.name}</h4>
      <p>{match.venue?.name}, {match.venue?.city}</p>
      <p>Status: {match.status}</p>
      <p>Starts: {new Date(match.starting_at).toLocaleString()}</p>
    </div>
  );

  return (
    <div className="team-matches">
      <h2>Team Matches</h2>
      {loading ? (
        <p>Loading matches...</p>
      ) : (
        <>
          <h3>Live</h3>
          <div className="match-list">
            {filterByStatus(LIVE_STATUSES).map(renderMatchCard)}
          </div>

          <h3>Upcoming</h3>
          <div className="match-list">
            {filterByStatus(UPCOMING_STATUSES).map(renderMatchCard)}
          </div>

          <h3>Completed</h3>
          <div className="match-list">
            {filterByStatus(COMPLETED_STATUSES).map(renderMatchCard)}
          </div>
        </>
      )}
    </div>
  );
};

export default TeamMatches;
