import React, { useEffect, useState } from 'react';
import './Teams.css';
import { useNavigate } from 'react-router-dom';

const Teams = () => {
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const res = await fetch(`/api/teams`);
        const json = await res.json();
        setTeams(json.data || []);
      } catch (err) {
        console.error('Error fetching teams', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeams();
  }, []);

  const internationalTeams = teams.filter(t => t.national_team);
  const domesticTeams = teams.filter(t => !t.national_team);

  const renderTeamSection = (title, teamList) => (
    <>
      <h3>{title}</h3>
      <div className="teams-grid">
        {teamList.map(team => (
          <div
            key={team.id}
            className="team-card"
            onClick={() => navigate(`/teams/${team.name}/series`)}
          >
            <img src={team.image_path} alt={team.name} />
            <p>{team.name}</p>
          </div>
        ))}
      </div>
    </>
  );

  return (
    <div className="teams-page">
      <h2>üèè Teams</h2>
      {loading ? (
        <p>Loading teams...</p>
      ) : (
        <>
          {renderTeamSection('üåç International Teams', internationalTeams)}
          {renderTeamSection('üè† Domestic & Franchise Teams', domesticTeams)}
        </>
      )}
    </div>
  );
};

export default Teams;
