import React, { useEffect, useState } from 'react';
import { NewsProvider, useNews } from '../NewsContext';
import './../css/NewsTab.css';

const TeamNews = ({ teamName }) => {
  const { articles, loading } = useNews();

  const teamNameData = `${teamName} Cricket Team`;

  if (!articles || articles.length === 0) {
    return (
      <div style={{ padding: '20px' }}>
        <p>No articles at the moment.</p>
      </div>
    );
  }

  return (
    <div className="news-container">
      <div className="news-grid">
        {articles.map((article, index) => (
          <div key={index} className="news-card">
            <img
              src={article.image}
              alt={article.title}
              className="news-image"
            />
            <h2 className="news-heading">{article.title}</h2>
            <p className="news-meta">
              {article.source.name} •{' '}
              {new Date(article.publishedAt).toLocaleString()}
            </p>
            <p className="news-description">{article.description}</p>
            <a
              href={article.url}
              target="_blank"
              rel="noreferrer"
              className="news-link"
            >
              Read more →
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default function News() {
  return (
    <NewsProvider query={`${teamNameData}`}>
      <NewsContent />
    </NewsProvider>
  );
}
