import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getTossWinnerName, getTossDecision, getTeamRuns, matchYear, matchStageName, venueName } from './../../utils/matchUtils';
import {
  liveStatuses,
  completedMatchStatuses,
  upcomingMatchStatuses,
} from './../../constants/matchStatusConstants';
import CountdownTimer from './../CountdownTimer';
import NotificationButton from './../NotificationButton';
import { useSchedule } from './../../context/ScheduleContext';
import './css/FeaturedMatches.css';

const FeaturedMatches = () => {
  const { matches, loading } = useSchedule();
  const [featuredMatches, setFeaturedMatches] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        if (loading || !Array.isArray(matches)) return;

        const liveMatches = matches.filter(m => liveStatuses.includes(m.status));
        const upcomingMatches = matches.filter(m => upcomingMatchStatuses.includes(m.status));
        const completedMatches = matches.filter(m => completedMatchStatuses.includes(m.status));

        const featured = [];

        // Add up to 2 live matches first
        featured.push(...liveMatches.slice(0, 2));

        // Track how many more we need to make 3
        let remaining = 3 - featured.length;

        // Add 1 upcoming match if available and needed
        if (remaining > 0 && upcomingMatches.length > 0) {
          featured.push(upcomingMatches[0]);
          remaining--;
        }

        // Add completed match if still needed
        if (remaining > 0 && completedMatches.length > 0) {
          featured.push(completedMatches[0]);
          remaining--;
        }

        // Gather remaining matches in extras
        const extras = [
          ...liveMatches.slice(2),
          ...upcomingMatches.slice(1),
          ...completedMatches.slice(1),
        ];

        // If still not 3 featured matches (in extreme edge cases), fill from extras
        while (featured.length < 3 && extras.length > 0) {
          featured.push(extras.shift());
        }

        setFeaturedMatches(featured);
      } catch (err) {
        console.error('Error fetching featured matches:', err);
      }
    };

    fetchMatches();
  }, [matches, loading]);

  if (!featuredMatches) return <div>Loading featured matches....</div>;
  if (loading) return <p>Loading featured matches...</p>;
  if (!featuredMatches.length) return <p>No matches found.</p>;

  return (
    <>
      <div className="live-matches-header">
        <h3>üèè Top Matches</h3>
        <a href="/schedule">View All</a>
      </div>

      <div className="match-cards-row">
        {featuredMatches.map(match => (
¬† ¬† ¬† ¬† ¬† <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`)}>
            <div className="match-header-top">
              {match.type && (
                <span className="match-type-tag">{match.type}</span>
              )}

              {upcomingMatchStatuses.includes(match.status) && 
                <NotificationButton match={match} />
              }
            </div>

            <p>{matchStageName(match)} - {match.round}</p>

            <p>
              <img src={match.localteam.image_path} alt="" className="flag-icon" />
              {match.localteam.name} {getTeamRuns(match, match.localteam.id) && (getTeamRuns(match, match.localteam.id))}
            </p>
            <p>
              <img src={match.visitorteam.image_path} alt="" className="flag-icon" />
              {match.visitorteam.name} {getTeamRuns(match, match.visitorteam.id) && (getTeamRuns(match, match.visitorteam.id))}
            </p>

            {match.status === 'NS' && (
              <CountdownTimer startTime={match.starting_at} offsetMs={600000} />
            )}

            {match?.note ? (
              <p className="status">{match.note}</p>
            ) : (
              match?.toss_won_team_id && match?.elected && (
                <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>
              )
            )}

            <div className="match-meta">
              <span className="venue">Venue: {venueName(match)}</span>
            </div>

            <div className="notify-btn">
              {match?.id && !upcomingMatchStatuses.includes(match.status) && !otherMatchStatuses.includes(match.status) && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    navigate(`/match/${match.id}`);
                  }}
                >
                  Scorecard
                </button>
              )}

              {match?.id && match?.stage_id && match.season_id && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    navigate(`/series/${match.stage_id}/${match.season_id}`, {
                      state: { seriesLabel: matchStageName(match), seriesCode: match?.type },
                    });
                  }}
                >
                  Series
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default FeaturedMatches;
