import React from 'react';
import './CommentaryTab.css';

const CommentaryTab = ({ commentary }) => {
  if (!commentary) return <p>No commentary available.</p>;

  return (
    <div className="commentary-list">
      {commentary.map((entry, idx) => (
        <div key={idx} className="commentary-entry">
          <div className="overs">
            Over {entry.over}.{entry.ball}
          </div>
          <div className="details">
            <strong>{entry.batsman.name}</strong> &nbsp;
            <em>vs</em> &nbsp;
            <strong>{entry.bowler.name}</strong>:&nbsp;
            {entry.runs} runs{entry.dismissal ? ` – ${entry.dismissal}` : ''}
            {entry.extras ? ` + ${entry.extras} extras` : ''}
          </div>
        </div>
      ))}
    </div>
  );
};

export default CommentaryTab;
