import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import CountdownTimer from './../CountdownTimer';
import NotificationButton from './../NotificationButton';
import './css/SeriesMatchesTab.css';

export default function SeriesMatchesTab({ seriesId }) {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();
  const stageIds = location.state?.stageIds || [seriesId]; // fallback to single stage

  useEffect(() => {
    const fetchAllMatches = async () => {
      try {
        const allMatches = [];

        for (const stageId of stageIds) {
          const res = await fetch(`/api/series/${stageId}/matches`);
          const json = await res.json();
          if (Array.isArray(json?.data?.data)) {
            allMatches.push(...json.data.data);
          }
        }

        setMatches(allMatches);
      } catch (err) {
        console.error('Error fetching series matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchAllMatches();
  }, [stageIds]);

  const liveStatuses = ['1st Innings', '2nd Innings', '3rd Innings', '4th Innings', 'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4', 'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.', 'Delayed'];
  const completedStatuses = ['Finished', 'Cancl', 'Aban.'];
  const upcomingStatuses = ['NS', 'Postp.'];

  const getTeamRuns = (match, teamId) => {
    if (!match.runs || !Array.isArray(match.runs)) return '';
    const innings = match.runs.filter(r => r.team_id === teamId);
    return innings.map(r => `${r.score}/${r.wickets} (${r.overs} ov)`).join(' & ');
  };

  const getTossWinnerName = (match) => {
    if (match.toss_won_team_id === match.localteam.id) return match.localteam.name;
    if (match.toss_won_team_id === match.visitorteam.id) return match.visitorteam.name;
    return '';
  };

  const getTossDecision = (match) => {
    if (match.elected === 'batting') return 'bat first';
    if (match.elected === 'bowling') return 'bowl first';
    return '';
  };

  const venueName = (match) => {
    return match.venue?.name && match.venue?.city
      ? `${match.venue.name}, ${match.venue.city}`
      : 'TBD';
  };

  if (loading) return <p>Loading matches...</p>;
  if (!matches.length) return <p>No matches found.</p>;

  return (
    <div className="match-cards-row">
      {matches.map(match => (
        <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Series' } })}>
          <div style={{ marginTop: '10px' }}>
            {match.status !== 'Finished' && <NotificationButton match={match} />}
          </div>
          <p><img src={match.localteam.image_path} alt="" className="flag-icon" /> {match.localteam.name} {getTeamRuns(match, match.localteam.id)}</p>
          <p><img src={match.visitorteam.image_path} alt="" className="flag-icon" /> {match.visitorteam.name} {getTeamRuns(match, match.visitorteam.id)}</p>
          {match.status === 'NS' && !match?.elected && <CountdownTimer startTime={match.starting_at} />}
          {match?.note ? <p className="status">{match.note}</p> : (match?.toss_won_team_id && match?.elected && <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>)}
          <div className="match-meta"><span className="venue">Venue: {venueName(match)}</span></div>
          {match.id && !upcomingStatuses.includes(match.status) && !liveStatuses.includes(match.status) && (
            <div className="notify-btn">
              <button onClick={(e) => { e.stopPropagation(); navigate(`/match/${match.id}`); }}>Scorecard</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
