import React, { useEffect, useState, useRef } from 'react';
import './Players.css';

export default function Players() {
  const [players, setPlayers] = useState([]);         // all loaded players (starting with 10)
  const [displayedPlayers, setDisplayedPlayers] = useState([]); // paginated display
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [countries, setCountries] = useState({});
  const [positions, setPositions] = useState({});
  const [modalPlayer, setModalPlayer] = useState(null);
  const [modalDetails, setModalDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingModal, setLoadingModal] = useState(false);
  const searchTimeout = useRef(null);

  // 🔹 Initial load: 10 players + country/position
  useEffect(() => {
    const loadInitial = async () => {
      try {
        setLoading(true);
        const [playerRes, countryRes, positionRes] = await Promise.all([
          fetch('/api/players').then(r => r.json()),
          fetch('/api/countries').then(r => r.json()),
          fetch('/api/positions').then(r => r.json()),
        ]);

        const countriesMap = {};
        countryRes.data.forEach(c => (countriesMap[c.id] = c));
        setCountries(countriesMap);

        const positionsMap = {};
        positionRes.data.forEach(p => (positionsMap[p.id] = p.name));
        setPositions(positionsMap);

        setPlayers(playerRes.data);
        setDisplayedPlayers(playerRes.data.slice(0, 10));
      } catch (err) {
        console.error('❌ Failed to load data:', err);
      } finally {
        setLoading(false);
      }
    };

    loadInitial();
  }, []);

  // 🔹 Load More Button Logic
  const handleLoadMore = () => {
    const next = players.slice(displayedPlayers.length, displayedPlayers.length + 10);
    setDisplayedPlayers(prev => [...prev, ...next]);
  };

  // 🔹 Debounced Search
  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    setSearchResults([]);

    if (searchTimeout.current) clearTimeout(searchTimeout.current);
    if (!term.trim()) {
      setDisplayedPlayers(players.slice(0, 10)); // reset view if cleared
      return;
    }

    searchTimeout.current = setTimeout(async () => {
      try {
        const res = await fetch(`/api/players/search?name=${encodeURIComponent(term)}`);
        const data = await res.json();
        setSearchResults(data.data || []);
      } catch (err) {
        console.error('❌ Search failed:', err);
      }
    }, 300);
  };

  // 🔹 Open Player Modal
  const openModal = async (player) => {
    setModalPlayer(player);
    setLoadingModal(true);
    try {
      const res = await fetch(`/api/player/${player.id}`);
      const data = await res.json();
      setModalDetails(data);
    } catch (err) {
      console.error('❌ Failed to load player details:', err);
    } finally {
      setLoadingModal(false);
    }
  };

  // 🔹 Select from Suggestions
  const handleSuggestionClick = (player) => {
    setSearchTerm('');
    setSearchResults([]);
    setDisplayedPlayers([player]);
  };
console.log(modalDetails);

  return (
    <div className="players-container">
      <h2>Players</h2>

      <input
        type="text"
        placeholder="Search players..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="search-bar"
      />

      {searchResults.length > 0 && (
        <ul className="search-suggestions">
          {searchResults.map(p => (
            <li key={p.id} onClick={() => handleSuggestionClick(p)}>
              <img src={p.image_path} alt={p.fullname} className="suggestion-img" />
              {p.fullname}
            </li>
          ))}
        </ul>
      )}

      {loading ? (
        <p>Loading players...</p>
      ) : (
        <>
          <div className="players-grid">
            {displayedPlayers.map(player => {
              const country = countries[player.country_id];
              return (
                <div key={player.id} className="player-card" onClick={() => openModal(player)}>
                  <img src={player.image_path} alt={player.fullname} className="player-img" />
                  <h4>{player.fullname}</h4>
                  {country && (
                    <div className="flag-info">
                      <img src={country.image_path} alt={country.name} className="player-flag" />
                      <span>{country.name}</span>
                    </div>
                  )}
                </div>
              );
            })}
          </div>

          {!searchTerm && displayedPlayers.length < players.length && (
            <button className="load-more-btn" onClick={handleLoadMore}>
              Load More
            </button>
          )}
        </>
      )}

{modalPlayer && (
  <div className="modal-overlay" onClick={() => setModalPlayer(null)}>
    <div className="modal" onClick={e => e.stopPropagation()}>
      {loadingModal ? (
        <p>Loading player details...</p>
      ) : modalDetails ? (
        <>
          <button className="close-btn" onClick={() => setModalPlayer(null)}>✖</button>
          <h3>{modalDetails.fullname}</h3>
          <img src={modalDetails.image_path} alt={modalDetails.fullname} className="player-modal-img" />
          <p><strong>Country:</strong> {modalDetails.country?.name || 'N/A'}</p>
          <p><strong>Position:</strong> {modalDetails.position?.name || 'N/A'}</p>
          <p><strong>DOB:</strong> {modalDetails.dateofbirth || '—'}</p>
          <p><strong>Batting:</strong> {modalDetails.battingstyle || '—'}</p>
          <p><strong>Bowling:</strong> {modalDetails.bowlingstyle || '—'}</p>
        </>
      ) : (
        <p>Failed to load player details.</p>
      )}
    </div>
  </div>
)}


    </div>
  );
}
