import React, { useEffect, useRef } from 'react';
import './CommentaryTab.css';

const emojis = {
  catch: '✋',
  bowled: '🎯',
  lbw: '🚷',
  runout: '🏃',
  retired: '💤',
  default: '☠️',
  bat: '🏏',
};

const formatDismissal = (text) => {
  if (!text) return '';
  const lower = text.toLowerCase();
  const icon = lower.includes('catch') ? emojis.catch :
               lower.includes('bowled') ? emojis.bowled :
               lower.includes('lbw') ? emojis.lbw :
               lower.includes('run out') ? emojis.runout :
               lower.includes('retired') ? emojis.retired :
               emojis.default;

  return `${icon} ${text.charAt(0).toUpperCase()}${text.slice(1)}`;
};

const CommentaryTab = ({ commentary, scorecard }) => {
  const latestInningRef = useRef(null);

  useEffect(() => {
    if (latestInningRef.current) {
      latestInningRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, []);

  if (!commentary?.length) return <p>No commentary available.</p>;

  const grouped = {};
  commentary.forEach(entry => {
    const key = `${entry.inning}-${entry.over}`;
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(entry);
  });

  const innings = Object.entries(grouped)
    .map(([key, balls]) => {
      const [inningId, over] = key.split('-').map(Number);
      return { inning: inningId, over, balls };
    })
    .sort((a, b) => {
      if (b.inning !== a.inning) return b.inning - a.inning;
      return b.over - a.over;
    });

  const getBatsmanScore = (inning, batsmanId) => {
    const inningData = scorecard?.[inning]?.batsmen?.find(b => b.player_id === batsmanId);
    return inningData ? `${inningData.runs} (${inningData.balls})` : '';
  };

const getOverSummary = (inningNum, overNum) => {
  const inning = scorecard?.[inningNum];
  if (!inning) return null;

  const bowlers = inning.bowlers || [];
  const bowler =
    bowlers.find(b => b.overs?.startsWith(overNum.toString())) || bowlers[0];

  const overBalls = commentary.filter(
    e => e.inning === inningNum && e.over === overNum
  );

  const batsmen = [...new Set(overBalls.map(b => b.batsman?.id).filter(Boolean))];
  const batsmenStr = batsmen
    .map(id => {
      const b = inning?.batsmen?.find(x => x.player_id === id);
      return b ? `${b.name} (${b.runs}/${b.balls})` : '';
    })
    .filter(Boolean)
    .join(', ');

  return {
    bowlerName: bowler?.name || 'Unknown',
    batsmenStr,
    extras: inning.extras?.r + (inning.extras?.b || 0) || 0,
  };
};

  return (
    <div className="commentary-tab">
      {innings.map((group, i) => {
        const isLatestInning = i === 0;
        const overBalls = [...group.balls].sort((a, b) => b.ball - a.ball);
        const overRuns = overBalls.reduce((sum, b) => sum + (b.runs || 0) + (b.extras || 0), 0);

        const summary = getOverSummary(group.inning, group.over);

        return (
          <div
            key={`inning-${group.inning}-over-${group.over}`}
            ref={isLatestInning ? latestInningRef : null}
            className="over-block"
          >
            <div className="over-header">Over {group.over + 1}</div>

            <div className="over-progress">
              {overBalls.slice().reverse().map((b, idx) => {
                const cls =
                  b.runs === 0 && !b.extras ? 'dot' :
                  b.runs >= 4 ? 'boundary' :
                  b.extras ? 'extra' : 'run';
                return (
                  <span key={idx} className={`ball ${cls}`}>
                    {b.runs || (b.extras ? 'E' : '•')}
                  </span>
                );
              })}
              <span className="circle">{overRuns} in {group.over + 1} over</span>
            </div>

            {overBalls.map((ball, idx) => {
              const batsman = ball.batsman?.name || 'Unknown';
              const bowler = ball.bowler?.name || 'Unknown';
              const dismissal = ball.dismissal ? formatDismissal(ball.dismissal) : '';
              const isOut = !!ball.dismissal;

              return (
                <div key={idx} className={`commentary-entry ${isOut ? 'dismissal' : ''}`}>
                  <div className="ball-title">
                    <strong>Over {ball.over}.{ball.ball}</strong> –&nbsp;
                    {dismissal ? (
                      <span className="out-line">
                        <strong>{bowler}</strong> <em>to</em> <strong>{batsman}</strong> – OUT – {dismissal}
                      </span>
                    ) : (
                      <>
                        {emojis.bat} <strong>{bowler}</strong> <em>to</em> <strong>{batsman}</strong> –&nbsp;
                        <span className="runs">{ball.runs} run{ball.runs !== 1 ? 's' : ''}</span>
                        {ball.extras ? (
                          <span className="extras"> + {ball.extras} extra{ball.extras !== 1 ? 's' : ''}</span>
                        ) : null}
                      </>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
};

export default CommentaryTab;
