import React, { useEffect, useState } from 'react';
import './SeriesPointsTab.css';

const SeriesPointsTab = ({ seriesId }) => {
  const [points, setPoints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [fallbackImg, setFallbackImg] = useState(null);

  useEffect(() => {
    const fetchPoints = async () => {
      try {
        const res = await fetch(`/api/series/${seriesId}/points`);
        const json = await res.json();
        setPoints(json.data || []);
      } catch (err) {
        console.error('Failed to fetch points table', err);
      } finally {
        setLoading(false);
      }
    };

    if (seriesId) fetchPoints();
  }, [seriesId]);

  if (loading) return <p>Loading points table...</p>;
  if (!points.length) return <p>No points table available for this match's series.</p>;

  return (
    <div className="points-table">
      <h3>Points Table</h3>
      <table>
        <thead>
          <tr>
            <th>Team</th>
            <th>M</th>
            <th>W</th>
            <th>L</th>
            <th>T</th>
            <th>NR</th>
          </tr>
        </thead>
        <tbody>
          {points.map((team, i) => (
            <tr key={i}>
              <td>
                <img src={team.img} alt={team.name} width={24} /> {team.teamname}
              </td>
              <td>{team.matches}</td>
              <td>{team.wins}</td>
              <td>{team.loss}</td>
              <td>{team.ties}</td>
              <td>{team.nr}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SeriesPointsTab;
