// components/Breadcrumb.js
import React from 'react';
import { useLocation, useParams } from 'react-router-dom';

const Breadcrumb = () => {
  const location = useLocation();
  const { id } = useParams();
  const state = location.state || {};
  const path = location.pathname;

  const crumbs = [{ name: 'Home', link: '/' }];

  if (path.startsWith('/teams')) {
    crumbs.push({ name: 'Teams', link: '/teams' });

    if (path.includes('/series') && state.teamName) {
      crumbs.push({ name: state.teamName, link: null });
    } else if (id) {
      crumbs.push({ name: id, link: null });
    }

  } else if (path.startsWith('/series')) {
    crumbs.push({ name: 'Series', link: '/series' });
    if (state.seriesName) {
      crumbs.push({ name: state.seriesName, link: null });
    }
  } else if (path.startsWith('/player')) {
    crumbs.push({ name: 'Players', link: '/players' });
    if (state.playerName) {
      crumbs.push({ name: state.playerName, link: null });
    }
  } else if (path.startsWith('/rankings')) {
    crumbs.push({ name: 'Rankings', link: null });
  } else if (path.startsWith('/stadiums')) {
    crumbs.push({ name: 'Stadiums', link: null });
  } else if (path.startsWith('/news')) {
    crumbs.push({ name: 'News', link: null });
  }

  return (
    <nav className="breadcrumb">
      {crumbs.map((c, idx) => (
        <span key={idx}>
          {c.link ? <a href={c.link}>{c.name}</a> : <strong>{c.name}</strong>}
          {idx < crumbs.length - 1 && ' › '}
        </span>
      ))}
    </nav>
  );
};

export default Breadcrumb;
