import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './css/PartnershipTab.css';

export default function PartnershipTab({ matchId }) {
  const [data, setData] = useState(null);
  const [activeTab, setActiveTab] = useState(null);

  useEffect(() => {
    axios.get(`/api/match/${matchId}`).then(res => {
      const matchData = res.data.data;
      setData(matchData);

      const scoreboards = matchData.scoreboards || [];
      const inningsList = [...new Set(scoreboards.map(s => s.scoreboard))];

      // Set first tab as active by default
      if (inningsList.length > 0) {
        setActiveTab(inningsList[0]);
      }
    });
  }, [matchId]);

  if (!data) return <div>Loading...</div>;
  const scorecard = data.scoreboards || [];
  const commentary = data.balls || [];

  if (!commentary || !scorecard) {
    return <p>No partnership data available.</p>;
  }

  const groupedByInnings = commentary.reduce((acc, ball) => {
    if (!acc[ball.inning]) acc[ball.inning] = [];
    acc[ball.inning].push(ball);
    return acc;
  }, {});

  const getDismissalType = (ball) => {
    if (ball.score?.name?.includes('run out')) return 'Run Out';
    if (ball.score?.name?.includes('bowled')) return 'Bowled';
    if (ball.score?.name?.includes('lbw')) return 'LBW Out';
    if (ball.score?.name?.includes('caught')) return 'Catch Out';
    if (ball.score?.name?.toLowerCase().includes('retired')) return 'Retired Hurt';
    return ball.score?.name || '-';
  };

  const generatePartnerships = (balls) => {
    const partnerships = [];
    let striker = null;
    let nonStriker = null;
    let current = { batsmen: [], runs: 0, balls: 0, dismissal: null };

    for (const ball of balls) {
      if (!striker && ball.batsman?.fullname) {
        striker = ball.batsman.fullname;
        nonStriker = ball.non_striker?.fullname || 'Unknown';
        current.batsmen = [striker, nonStriker];
      }

      current.runs += Number(ball.score?.runs || 0);
      current.balls++;

      if (ball.wicket) {
        const dismissed = ball.batsman?.fullname;
        current.dismissal = getDismissalType(ball);

        partnerships.push({ ...current });
        current = { batsmen: [], runs: 0, balls: 0, dismissal: null };

        if (dismissed === striker) {
          striker = ball.next_batsman?.fullname || null;
        } else if (dismissed === nonStriker) {
          nonStriker = ball.next_batsman?.fullname || null;
        }

        if (striker && nonStriker) {
          current.batsmen = [striker, nonStriker];
        }
      }
    }

    if (current.batsmen.length === 2) {
      partnerships.push(current);
    }

    return partnerships;
  };

  return (
    <div className="partnership-tab">
      {Object.entries(groupedByInnings).map(([inning, balls]) => {
        const teamName = scorecard?.innings?.find(i => i.number == inning)?.team?.name || `Inning ${inning}`;
        const sortedBalls = balls.sort((a, b) => parseFloat(b.ball) - parseFloat(a.ball)); // latest first
        const partnerships = generatePartnerships(sortedBalls.reverse()); // process chronologically

        return (
          <div key={inning} className="inning-partnership">
            <h3>{teamName} - Inning {inning}</h3>
            <table className="partnership-table">
              <thead>
                <tr>
                  <th>Batsmen</th>
                  <th>Runs</th>
                  <th>Balls</th>
                  <th>Dismissal</th>
                </tr>
              </thead>
              <tbody>
                {partnerships.map((p, i) => (
                  <tr key={i}>
                    <td>{p.batsmen.join(' & ')}</td>
                    <td>{p.runs}</td>
                    <td>{p.balls}</td>
                    <td>{p.dismissal || '-'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      })}
    </div>
  );
}
