import React, { useEffect, useState } from 'react';
import './FeaturedMatches.css';
import { useNavigate } from 'react-router-dom';

const FeaturedMatches = ({ matches = [] }) => {
  const navigate = useNavigate();

  const [activeStage, setActiveStage] = useState('All');
  const [groupedMatches, setGroupedMatches] = useState({});
  const [scrollPage, setScrollPage] = useState(0);

  const now = new Date();
  const twoDaysAgo = new Date(now);
  twoDaysAgo.setDate(now.getDate() - 2);

  useEffect(() => {
    const live = matches.filter(m => m.status === 'LIVE');
    const upcoming = matches.filter(m => m.status === 'UPCOMING');
    const completed = matches.filter(m =>
      m.status === 'COMPLETED' &&
      new Date(m.start_time) >= twoDaysAgo
    );

    const all = [...live, ...upcoming, ...completed];

    const byStage = {};
    all.forEach(match => {
      const stage = match.stage || 'Others';
      if (!byStage[stage]) byStage[stage] = [];
      byStage[stage].push(match);
    });

    setGroupedMatches({ All: all, ...byStage });
  }, [matches]);

  const stages = Object.entries(groupedMatches)
    .filter(([stage, list]) => list.length > 0)
    .map(([stage]) => stage);

  const visibleMatches = groupedMatches[activeStage] || [];

  const cardsPerPage = 3;
  const totalPages = Math.ceil(visibleMatches.length / cardsPerPage);

  const paginated = visibleMatches.slice(
    scrollPage * cardsPerPage,
    (scrollPage + 1) * cardsPerPage
  );

  const getMatchStatusColor = (status) => {
    switch (status) {
      case 'LIVE': return 'red';
      case 'UPCOMING': return 'blue';
      case 'COMPLETED': return 'gray';
      default: return 'gray';
    }
  };

  const totalMatches = Object.values(groupedMatches)
    .flat().filter((v, i, arr) =>
      arr.findIndex(m => m.id === v.id) === i
    ).length;

  return (
    <div className="featured-matches-wrapper">
      <div className="stage-header">
        <span className="tab active-tab">Matches ({totalMatches})</span>
        <div className="series-scroll">
          {stages.map(stage => (
            <span
              key={stage}
              className={`stage-pill ${activeStage === stage ? 'active' : ''}`}
              onClick={() => { setActiveStage(stage); setScrollPage(0); }}
            >
              {stage}
            </span>
          ))}
          <span className="stage-pill all-link" onClick={() => navigate('/schedule')}>All</span>
        </div>
      </div>

      <div className="match-cards-container">
        {paginated.map(match => (
          <div className="match-card" key={match.id}>
            <div className="match-header">
              <span className={`status ${getMatchStatusColor(match.status)}`}>
                {match.status}
              </span>
              <span className="series-name">{match.stage}</span>
            </div>
            <div className="team-block">
              <span>{match.team1}</span> vs <span>{match.team2}</span>
            </div>
            <div className="score-block">{match.score || 'Score info here'}</div>
            <div className="actions">
              <span onClick={() => navigate(`/match/${match.id}`)}>Schedule</span>
              <span>Scorecard</span>
              <span>Series</span>
            </div>
          </div>
        ))}
      </div>

      {totalPages > 1 && (
        <div className="pagination-dots">
          {Array.from({ length: totalPages }, (_, i) => (
            <span
              key={i}
              className={i === scrollPage ? 'dot active' : 'dot'}
              onClick={() => setScrollPage(i)}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default FeaturedMatches;
