import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './css/Schedule.css'; // Reuse existing styles

const FeaturedMatches = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch('/api/schedule');
        const json = await res.json();
        const data = json?.data || [];

        const liveMatches = data.filter(m => m.status && m.status !== 'NS' && m.status !== 'Finished');
        const upcomingMatches = data.filter(m => m.status === 'NS');
        const completedMatches = data.filter(m => m.status === 'Finished');

        const featured = [];
        if (liveMatches.length > 0) featured.push(liveMatches[0]);
        if (upcomingMatches.length > 0) featured.push(upcomingMatches[0]);
        if (completedMatches.length > 0) featured.push(completedMatches[0]);

        const extras = [
          ...liveMatches.slice(1),
          ...upcomingMatches.slice(1),
          ...completedMatches.slice(1),
        ];

        while (featured.length < 3 && extras.length > 0) {
          featured.push(extras.shift());
        }

        setMatches(featured);
      } catch (err) {
        console.error('Error fetching featured matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  const formattedDate = (input) => {
    if (!input) return 'N/A';
    try {
      return new Date(input).toLocaleString();
    } catch {
      return 'Invalid date';
    }
  };

  const getTeamRuns = (match, teamId) => {
    if (!match.runs || !Array.isArray(match.runs)) return '';
    const innings = match.runs.filter(r => r.team_id === teamId);
    return innings.map(r => `${r.score}/${r.wickets} (${r.overs} ov)`).join(' & ');
  };

  if (loading) return <p>Loading featured matches...</p>;
  if (!matches.length) return <p>No matches found.</p>;

  return (
    <>
      <div className="live-matches-header">
        <h3>üèè Top Matches</h3>
        <a href="/schedule">View All</a>
      </div>

      <div className="match-cards-row">
        {matches.map(match => (
          <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Home' } })}>
            <div className="series-line">
              <span>{match.league?.name}, {match.round}</span>
            </div>

            <div className="match-meta">
              <span>{match.localteam?.name} vs {match.visitorteam?.name}</span>
            </div>

            <div className="match-meta">
              <span className="venue">{match.venue?.name}, {match.venue?.city}</span>
            </div>

            {match.status !== 'NS' && (
              <div className="match-score">
                <p>{match.localteam.name}: {getTeamRuns(match, match.localteam.id)}</p>
                <p>{match.visitorteam.name}: {getTeamRuns(match, match.visitorteam.id)}</p>
              </div>
            )}

            {match.status === 'NS' && (
              <p className="status">Match starts at {formattedDate(match.starting_at)}</p>
            )}

            {match.status === 'Finished' && match.note && (
              <p className="status">{match.note}</p>
            )}

            {match.status === 'Finished' && match.manofmatch?.fullname && (
              <p className="status">Man of the match: {match.manofmatch.fullname}</p>
            )}

            {match.series_id && (
              <Link
                to={`/series/${match.series_id}`}
                className="series-link"
                onClick={(e) => e.stopPropagation()}
              >
                Series
              </Link>
            )}
          </div>
        ))}
      </div>
    </>
  );
};

export default FeaturedMatches;
