import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTeams } from './../TeamsContext';
import { useFallbackImages } from './../FallbackImageContext';
import './css/TeamFixtures.css';

const TeamFixtures = () => {
  const { teams, filteredTeams, loading } = useTeams();
  const navigate = useNavigate();



  if (loading) return <div>Loading...</div>;

  return (
    <div className="home-teams-container">
      <div className="header">
        <h3>üèÅ Team Fixtures</h3>
        <a href="/teams">
          View All
        </a>
      </div>

      <div className="home-teams-grid">
        {filteredTeams.slice(0, 9).filter(team => team.national_team).sort((a, b) => a.name.localeCompare(b.name)).map((team) => (
          <div
            key={team.id}
            className="home-team-card"
            onClick={() => navigate(`/teams/${team.id}/matches`, { state: { teamName: team.name } })}
          >
            <img
              src={
                team.image_path && team.image_path.endsWith('cdn.sportmonks.com')
                  ? fallbackImg || '' // wait for fallbackImg
                  : team.image_path
              }
              alt={team.name}
            />
              <p>{team.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeamFixtures;
