// frontend/src/pages/News.js
import React, { useEffect, useState } from 'react';

export default function News() {
  const [articles, setArticles] = useState([]);
  const API_KEY = 'your_gnews_api_key'; // Replace with your actual GNews API key

  useEffect(() => {
    fetch(`https://gnews.io/api/v4/search?q=cricket&lang=en&max=10&apikey=${API_KEY}`)
      .then(res => res.json())
      .then(data => setArticles(data.articles || []))
      .catch(console.error);
  }, []);

  return (
    <div className="p-6 space-y-6">
      <h1 className="text-2xl font-bold mb-4">ðŸ“° Latest Cricket News</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {articles.map((article, index) => (
          <div key={index} className="bg-white rounded-lg shadow p-4 hover:shadow-lg transition-shadow">
            <img
              src={article.image}
              alt={article.title}
              className="w-full h-48 object-cover rounded mb-3"
            />
            <h2 className="text-lg font-semibold line-clamp-2">{article.title}</h2>
            <p className="text-sm text-gray-600 mb-2">
              {article.source.name} â€¢ {new Date(article.publishedAt).toLocaleString()}
            </p>
            <p className="text-sm line-clamp-3">{article.description}</p>
            <a
              href={article.url}
              target="_blank"
              rel="noreferrer"
              className="block mt-2 text-blue-600 font-medium"
            >
              Read more â†’
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}
