import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function SeriesStandingsTab({ seriesId, seasonId }) {
  const [standings, setStandings] = useState([]);
  const [teams, setTeams] = useState({});

  useEffect(() => {
    const fetchStandingsAndTeams = async () => {
      try {
        const standingsRes = await axios.get(`/api/series/${seasonId}/standings`);
        const data = standingsRes.data.data || [];
        setStandings(data);

        // Extract unique team_ids
        const uniqueTeamIds = [...new Set(data.map(row => row.team_id))];

        const teamMap = {};
        for (const id of uniqueTeamIds) {
          try {
            const res = await axios.get(`/api/teams/${id}`);
            teamMap[id] = res.data.data.data.name;
          } catch (e) {
            teamMap[id] = 'Unknown';
          }
        }
        setTeams(teamMap);
      } catch (e) {
        console.error('❌ Error loading standings or teams:', e.message);
      }
    };

    fetchStandingsAndTeams();
  }, [seriesId, seasonId]);

  return (
    <table border="1" cellPadding="5">
      <thead>
        <tr>
          <th>Team</th>
          <th>Played</th>
          <th>Won</th>
          <th>Lost</th>
          <th>Points</th>
          <th>NRR</th>
        </tr>
      </thead>
      <tbody>
        {standings.map(row => (
          <tr key={row.team_id}>
            <td>{teams[row.team_id]}</td>
            <td>{row.won + row.lost + row.draw + row.noresult}</td>
            <td>{row.won}</td>
            <td>{row.lost}</td>
            <td>{row.points}</td>
            <td>{row.netto_run_rate}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
