import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import CountdownTimer from './CountdownTimer';
import NotificationButton from './NotificationButton';
import './css/Schedule.css';

const Schedule = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [fallbackImg, setFallbackImg] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch('/api/schedule');
        const json = await res.json();
        setMatches(json.data || []);
      } catch (err) {
        console.error('Error fetching matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  useEffect(() => {
    const fetchFallback = async () => {
      try {
        const res = await fetch('/api/default-images');
        const text = await res.text();
        const match = text.match(/flag_url:\s*['"]([^'"]+)['"]/);
        if (match) setFallbackImg(match[1]);
      } catch (err) {
        console.error('Failed to fetch fallback image:', err);
      }
    };

    fetchFallback();
  }, []);

  const liveMatchStatuses = [
    '1st Innings', '2nd Innings', '3rd Innings', '4th Innings',
    'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4',
    'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.',
  ];

  const otherMatchStatuses = [
    'Delayed',
  ];

  const completedMatchStatuses = [
    'Finished', 'Cancl', 'Aban.',
  ];

  const upcomingMatchStatuses = [
    'NS', 'Postp.',
  ];

  const liveStatuses = [...liveMatchStatuses, ...otherMatchStatuses];
  const liveMatches = matches.filter(m => liveStatuses.includes(m.status));
  const upcomingMatches = matches.filter(m => upcomingMatchStatuses.includes(m.status));
  const completedMatches = matches.filter(m => completedMatchStatuses.includes(m.status));

  const matchYear = (match) => {
    return new Date(match.starting_at).getFullYear();
  };

  const matchStageName = (match) => {
    return match?.stage?.name + ', ' + matchYear(match);
  };

  const getTossWinnerName = (match) => {
    if (match.toss_won_team_id === match.localteam.id) {
      return match.localteam.name;
    } else if (match.toss_won_team_id === match.visitorteam.id) {
      return match.visitorteam.name;
    } else {
      return '';
    }
  };

  const getTossDecision = (match) => {
    if (match.elected === 'batting') {
      return 'bat first';
    } else if (match.elected === 'bowling') {
      return 'bowl first';
    } else {
      return '';
    }
  };

  const renderMatchCard = (match) => {
    const getTeamScores = (teamName) => {
      if (!match.score || !Array.isArray(match.score)) return '';
      const innings = match.score.filter(s =>
        s.inning?.toLowerCase().includes(teamName?.toLowerCase())
      );
      return innings.map(s => `${s.r}/${s.w} (${s.o} ov)`).join(' & ');
    };

    const getTeamRuns = (teamId) => {
      if (!match.runs || !Array.isArray(match.runs)) return '';
      const innings = match.runs.filter(r => r.team_id === teamId);
      return innings
        .map(r => `${r.score}/${r.wickets} (${r.overs} ov)`)
        .join(' & ');
    };

    return (
      <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Schedule' } })}>
        <div style={{ marginTop: '10px' }}>
          {match.status !== 'Finished' &&
            <NotificationButton match={match} />
          }
        </div>

        <div className="match-header">
          {match.type && (
            <span className="match-type-tag">{match.type}</span>
          )}

          <p>{matchStageName(match)}</p>

          {(match.status === 'NS' || match.status === 'Delayed' || match.status === 'Aban.') && (
            <>
              <p>
                <img src={match.localteam.image_path} alt="" className="flag-icon" />{match.localteam.name}
              </p>
              <p>
                <img src={match.visitorteam.image_path} alt="" className="flag-icon" />{match.visitorteam.name}
              </p>
            </>
          )}
        </div>

        {match.status !== 'NS' && (
          <div className="match-score">
            <p>
              <img src={match.localteam.image_path} alt="" className="flag-icon" />
              {match.localteam.name} {getTeamRuns(match.localteam.id) && (getTeamRuns(match.localteam.id))}
            </p>
            <p>
              <img src={match.visitorteam.image_path} alt="" className="flag-icon" />
              {match.visitorteam.name} {getTeamRuns(match.visitorteam.id) && (getTeamRuns(match.visitorteam.id))}
            </p>
          </div>
        )}

        {match.status === 'NS' && !match?.elected && (
          <CountdownTimer startTime={match.starting_at} />
        )}

        {match?.note ? (
          <p className="status">{match.note}</p>
        ) : (
          match?.toss_won_team_id && match?.elected && (
            <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>
          )
        )}

        {(match.venue?.name && match.venue?.city) ? (
          <p className="venue">{match.venue?.name}, {match.venue?.city}</p>
        ) : (
          <p className="venue">Venue: TBD</p>
        )}

        <div className="notify-btn">
          {match.id && !upcomingMatchStatuses.includes(match.status) && !otherMatchStatuses.includes(match.status) && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                navigate(`/match/${match.id}`);
              }}
            >
              Scorecard
            </button>
          )}

          {match?.id && match?.stage_id && match.season_id && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                navigate(`/series/${match.stage_id}/${match.season_id}`, {
                  state: { seriesLabel: matchStageName(match), seriesCode: match?.type },
                });
              }}
            >
              Series
            </button>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="schedule-container">
      <h2>Match Schedule</h2>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        <>
          <h3 className="subheader">🟢 Live Matches</h3>
          <div className="schedule-list">
            {liveMatches.length > 0 ? liveMatches.map(renderMatchCard) : <p>No live matches</p>}
          </div>

          <h3 className="subheader">🟡 Upcoming Matches</h3>
          <div className="schedule-list">
            {upcomingMatches.length > 0 ? upcomingMatches.map(renderMatchCard) : <p>No upcoming matches</p>}
          </div>

          <h3 className="subheader">🔴 Completed Matches</h3>
          <div className="schedule-list">
            {completedMatches.length > 0 ? completedMatches.map(renderMatchCard) : <p>No completed matches</p>}
          </div>
        </>
      )}
    </div>
  );
};

export default Schedule;
