// MatchNotification.js
import React, { useEffect, useState } from 'react';

const MatchNotification = ({ match }) => {
  const [enabled, setEnabled] = useState(false);
  const [lastStatus, setLastStatus] = useState(match.status);

  const notify = (title, body) => {
    if (Notification.permission === 'granted') {
      new Notification(title, { body });
    }
  };

  const handleToggle = () => {
    if (Notification.permission === 'default') {
      Notification.requestPermission().then(permission => {
        if (permission === 'granted') {
          setEnabled(prev => !prev);
        }
      });
    } else {
      setEnabled(prev => !prev);
    }
  };

  useEffect(() => {
    if (!enabled) return;

    if (match.status !== lastStatus) {
      setLastStatus(match.status);

      if (match.status === 'LIVE') {
        notify('üèè Match is LIVE!', `${match.localteam.name} vs ${match.visitorteam.name}`);
      }

      if (match.status === 'NS') {
        const matchTime = new Date(match.starting_at);
        const now = new Date();
        const diffMins = (matchTime - now) / 60000;

        if (diffMins < 10 && diffMins > 0) {
          notify('‚åõ Match starts in 10 mins', `${match.localteam.name} vs ${match.visitorteam.name}`);
        }
      }

      if (['Finished', 'Result'].includes(match.status)) {
        notify('‚úÖ Match Result Out!', `${match.localteam.name} vs ${match.visitorteam.name}`);
      }
    }
  }, [match.status, enabled]);

  return (
    <button
      className="notify-btn"
      onClick={e => {
        e.stopPropagation();
        handleToggle();
      }}
    >
      {enabled ? 'üîî On' : 'üîï Off'}
    </button>
  );
};

export default MatchNotification;
