import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './css/TeamMatches.css';

export default function TeamMatches() {
  const { id } = useParams();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [teamNames, setTeamNames] = useState({});
  const [activeTab, setActiveTab] = useState('live');

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await axios.get(`/api/teams/${id}/matches`);
        const allMatches = res.data.data || [];
        setMatches(allMatches);

        // Extract all team IDs
        const teamIds = new Set();
        allMatches.forEach((m) => {
          teamIds.add(m.localteam_id);
          teamIds.add(m.visitorteam_id);
        });

        const names = {};
        await Promise.all([...teamIds].map(async (teamId) => {
          try {
            const teamRes = await axios.get(`/api/teams/${teamId}`);
            names[teamId] = teamRes.data.data.data.name;
          } catch (err) {
            names[teamId] = 'Unknown';
          }
        }));

        setTeamNames(names);
      } catch (err) {
        console.error('Error loading matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [id]);

  const getTeamName = (teamId) => teamNames[teamId] || '...';

  const filterMatches = (type) => {
    const live = ['LIVE', '1st Innings', '2nd Innings', '3rd Innings', '4th Innings', 'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4', 'Tea Break', 'Lunch', 'Dinner', 'Innings Break', 'Int.', 'Delayed'];
    const upcoming = ['NS', 'Postp.'];
    const completed = ['Finished', 'Cancl', 'Aban.'];

    if (type === 'live') return matches.filter(m => live.includes(m.status));
    if (type === 'upcoming') return matches.filter(m => upcoming.includes(m.status));
    return matches.filter(m => completed.includes(m.status));
  };

  return (
    <div className="team-matches">
      <h2>Matches for Team {id}</h2>

      <div className="tabs">
        {['live', 'upcoming', 'completed'].map((tab) => (
          <button
            key={tab}
            className={activeTab === tab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        <div className="match-list">
          {filterMatches(activeTab).map((match) => (
            <div key={match.id} className="match-card">
              <h4>{getTeamName(match.localteam_id)} vs {getTeamName(match.visitorteam_id)}, {match.round}</h4>
              <p>{match.status}</p>
              <p>{match.starting_at}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
