import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './css/Schedule.css';

const Schedule = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [fallbackImg, setFallbackImg] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch('/api/schedule');
        const json = await res.json();
        setMatches(json.data || []);
      } catch (err) {
        console.error('Error fetching matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  useEffect(() => {
    const fetchFallback = async () => {
      try {
        const res = await fetch('/api/default-images');
        const text = await res.text();
        const match = text.match(/flag_url:\s*['"]([^'"]+)['"]/);
        if (match) setFallbackImg(match[1]);
      } catch (err) {
        console.error('Failed to fetch fallback image:', err);
      }
    };

    fetchFallback();
  }, []);

  const formattedDate = (input) => {
    if (!input) return 'N/A';
    try {
      return new Date(input).toLocaleString();
    } catch (e) {
      return 'Invalid date';
    }
  };

  const liveMatches = matches.filter(
    m => m.status && m.status !== 'NS' && m.status !== 'Finished' && m.status !== 'Aban.' && m.status !== 'Postp.' && m.status !== 'Cancl'
  );

  const upcomingMatches = matches.filter(
    m => m.status && m.status === 'NS'
  );

  const completedMatches = matches.filter(
    m => m.status && m.status === 'Finished' && m.status !== 'Aban.' && m.status !== 'Postp.' && m.status !== 'Cancl'
  );

  const renderMatchCard = (match) => {
    const getTeamScores = (teamName) => {
      if (!match.score || !Array.isArray(match.score)) return '';
      const innings = match.score.filter(s =>
        s.inning?.toLowerCase().includes(teamName?.toLowerCase())
      );
      return innings.map(s => `${s.r}/${s.w} (${s.o} ov)`).join(' & ');
    };

    const getTeamRuns = (teamId) => {
      if (!match.runs || !Array.isArray(match.runs)) return '';
      const innings = match.runs.filter(r => r.team_id === teamId);
      return innings
        .map(r => `${r.score}/${r.wickets} (${r.overs} ov)`)
        .join(' & ');
    };

    return (
      <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Schedule' } })}>
        <div className="match-header">
          <p>{match.league.name}, {match.round}</p>

          {match.status === 'NS' && (
            <p>{match.localteam.name} vs {match.visitorteam.name}</p>
          )}

          <p className="venue">{match.venue?.name}, {match.venue?.city}</p>
        </div>

        {match.status !== 'NS' && (
          <div className="match-score">
            {getTeamRuns(match.localteam.id) && (
              <p>{match.localteam.name}: {getTeamRuns(match.localteam.id)}</p>
            )}
            {getTeamRuns(match.visitorteam.id) && (
              <p>{match.visitorteam.name}: {getTeamRuns(match.visitorteam.id)}</p>
            )}
          </div>
        )}

        {match.status === 'NS' && (
          <p className="status">Match starts at {formattedDate(match.starting_at)}</p>
        )}

        {match.status === 'Finished' && match.status !== 'NS' && (
          <p className="status">{match.note}</p>
        )}

        {match.status === 'Finished' && match.manofmatch?.fullname && (
          <p className="status">Man of the match: {match.manofmatch?.fullname}</p>
        )}
      </div>
    );
  };

  return (
    <div className="schedule-container">
      <h2>Match Schedule</h2>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        <>
          <h3 className="subheader">🟢 Live Matches</h3>
          <div className="schedule-list">
            {liveMatches.length > 0 ? liveMatches.map(renderMatchCard) : <p>No live matches</p>}
          </div>

          <h3 className="subheader">🟡 Upcoming Matches</h3>
          <div className="schedule-list">
            {upcomingMatches.length > 0 ? upcomingMatches.map(renderMatchCard) : <p>No upcoming matches</p>}
          </div>

          <h3 className="subheader">🔴 Completed Matches</h3>
          <div className="schedule-list">
            {completedMatches.length > 0 ? completedMatches.map(renderMatchCard) : <p>No completed matches</p>}
          </div>
        </>
      )}
    </div>
  );
};

export default Schedule;
