// src/components/tab/LineupTab.js

import React from 'react';
import './LineupTab.css'; // optional: for styling

const LineupTab = ({ match }) => {
  const { lineup, localteam, visitorteam } = match;

  const localTeamPlayers = lineup.filter(player => player.team_id === localteam?.id);
  const visitorTeamPlayers = lineup.filter(player => player.team_id === visitorteam?.id);

  const renderPlayers = (players) => (
    <ul className="lineup-list">
      {players.map((player) => (
        <li key={player.id}>
          {player.fullname || player.firstname + ' ' + player.lastname}
          {player.captain && <span className="player-role"> (C)</span>}
          {player.wk && <span className="player-role"> (WK)</span>}
        </li>
      ))}
    </ul>
  );

  return (
    <div className="lineup-tab">
      <div className="team-lineup">
        <h3>{localteam?.name}</h3>
        {renderPlayers(localTeamPlayers)}
      </div>
      <div className="team-lineup">
        <h3>{visitorteam?.name}</h3>
        {renderPlayers(visitorTeamPlayers)}
      </div>
    </div>
  );
};

export default LineupTab;
