import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import CountdownTimer from './../CountdownTimer';
import News from './News.js';
import './../css/Matches.css';

export default function Matches() {
  const { id } = useParams();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [teamNames, setTeamNames] = useState({});
  const [venueData, setVenueData] = useState({});
  const [activeTab, setActiveTab] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/api/teams/${id}/matches`);
        const allFixtures = res.data.data.fixtures || [];
        const allResults = res.data.data.results || [];
        const allMatches = [...allFixtures, ...allResults];
        setMatches(allMatches);

        const teamIds = new Set();
        const venueIds = new Set();

        allMatches.forEach(m => {
          teamIds.add(m.localteam_id);
          teamIds.add(m.visitorteam_id);
          venueIds.add(m.venue_id);
        });

        const names = {};
        await Promise.all([...teamIds].map(async (teamId) => {
          try {
            const res = await axios.get(`/api/teams/${teamId}`);
            names[teamId] = res.data.data.data.name;
          } catch {
            names[teamId] = 'Unknown';
          }
        }));
        setTeamNames(names);

        const venues = {};
        await Promise.all([...venueIds].map(async (venueId) => {
          try {
            const res = await axios.get(`/api/venues/${venueId}`);
            venues[venueId] = res.data.data;
          } catch {
            venues[venueId] = {};
          }
        }));
        setVenueData(venues);
      } catch (err) {
        console.error('Failed to load matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const getTeamName = (tid) => teamNames[tid] || 'TBD';
  const getVenueName = (vid) => venueData[vid]?.name || '';
  const getVenueCity = (vid) => venueData[vid]?.city || '';

  const liveStatuses = ['LIVE', '1st Innings', '2nd Innings', '3rd Innings', '4th Innings', 'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4', 'Tea Break', 'Lunch', 'Dinner', 'Innings Break', 'Int.', 'Delayed'];
  const upcomingStatuses = ['NS', 'Postp.'];
  const completedStatuses = ['Finished', 'Cancl', 'Aban.'];
console.log(matches);
  const liveMatches = matches.filter(m => liveStatuses.includes(m.status));
  const upcomingMatches = matches.filter(m => upcomingStatuses.includes(m.status));
  const completedMatches = matches.filter(m => completedStatuses.includes(m.status));

  const tabData = [
    { key: 'live', label: 'Live', data: liveMatches },
    { key: 'upcoming', label: 'Upcoming', data: upcomingMatches },
    { key: 'completed', label: 'Completed', data: completedMatches },
    { key: 'news', label: 'News', data: [1] }, // Always visible
  ];

  const availableTabs = tabData.filter(t => t.key === 'news' || t.data.length > 0);
  const defaultTab = availableTabs.length > 0 ? availableTabs[0].key : 'completed';

  const matchYear = (match) => {
    return new Date(match.starting_at).getFullYear();
  };

  useEffect(() => {
    setActiveTab(defaultTab);
  }, [defaultTab]);

  const currentMatches = tabData.find(tab => tab.key === activeTab)?.data || [];

    const getTeamRuns = (match, teamId) => {
      if (!match.runs || !Array.isArray(match.runs)) return '';
      const innings = match.runs.filter(r => r.team_id === teamId);
      return innings
        .map(r => `${r.score}/${r.wickets} (${r.overs} ov)`)
        .join(' & ');
    };

  return (
    <div className="team-matches">
      <h2>{getTeamName(id)} Cricket Team</h2>

      <div className="tabs">
        {availableTabs.map(tab => (
          <button
            key={tab.key}
            className={activeTab === tab.key ? 'active' : ''}
            onClick={() => setActiveTab(tab.key)}
          >
            {tab.label}
          </button>
        ))}
      </div>

{loading ? (
  <p>Loading matches...</p>
) : activeTab === 'news' ? (
  <News teamName={getTeamName(id)} />
) : (
  <>
    {currentMatches.length === 0 && (
      <p>
        No {activeTab} matches available.
      </p>
    )}

    <div className="match-list">
      {currentMatches.map((match) => (
        <div
          key={match.id}
          className="match-card"
          onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Teams' } })}
        >
          <h4>{getTeamName(match.localteam_id)} vs {getTeamName(match.visitorteam_id)}, {match.round}, {matchYear(match)}</h4>

          {getVenueName(match.venue_id) && getVenueCity(match.venue_id) ? (
            <p>Venue: {getVenueName(match.venue_id)}, {getVenueCity(match.venue_id)}</p>
          ) : (
            <p>Venue: TBD</p>
          )}

          {match.status === 'NS' && (
            <CountdownTimer startTime={match.starting_at} />
          )}

          <p className="status">{match.note}</p>

          {match.status === 'Finished' && match.manofmatch?.fullname && (
            <p className="status">Man of the match: {match.manofmatch?.fullname}</p>
          )}

          <div className="notify-btn">
            {match.id && !upcomingStatuses.includes(match.status) && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  navigate(`/match/${match.id}`);
                }}
              >
                Scorecard
              </button>
            )}
          </div>
        </div>
      ))}
    </div>
  </>
)}

    </div>
  );
}
