import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/TopPlayers.css';

export default function TopPlayers() {
  const [playerNames, setPlayerNames] = useState([]);
  const [playerData, setPlayerData] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/api/default-player-images')
      .then(res => res.text())
      .then(text => {
        const names = text
          .split('\n')
          .map(n => n.trim())
          .filter(Boolean);
        setPlayerNames(names);
      });
  }, []);

  useEffect(() => {
    const loadPlayerData = async () => {
      setLoading(true);
      const results = [];

      for (const name of playerNames) {
        const cacheKey = `top-player-${name.toLowerCase()}`;
        const cached = sessionStorage.getItem(cacheKey);

        if (cached) {
          results.push(JSON.parse(cached));
          continue;
        }

        try {
          const res = await fetch(`/api/players?search=${encodeURIComponent(name)}`);
          const json = await res.json();
          const match = json?.data?.find(p => p.name.toLowerCase() === name.toLowerCase());
          if (match) {
            const player = {
              id: match.id,
              name: match.name,
              image: match.image,
            };
            sessionStorage.setItem(cacheKey, JSON.stringify(player));
            results.push(player);
          }
        } catch (err) {
          console.warn(`Failed to fetch ${name}:`, err.message);
        }
      }

      setPlayerData(results);
      setLoading(false);
    };

    if (playerNames.length > 0) {
      loadPlayerData();
    }
  }, [playerNames]);

  return (
    <div className="top-players-container">
      <div className="header">
        <h3>Top Players</h3>
        <a href="/player">
          View All
        </a>
      </div>

      {loading ? (
        <div className="top-players-loading">Loading top players...</div>
      ) : (
        <div className="top-players-grid">
          {playerData.map((player) => (
            <div
              key={player.id}
              className="top-player-card"
              onClick={() => navigate(`/player/${player.id}`)}
            >
              <img src={player.image} alt={player.name} />
              <p>{player.name}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
