// src/components/Series.js
import React, { useEffect, useState, useMemo } from 'react';
import { Link } from 'react-router-dom';
import './css/Series.css';

export default function Series() {
  const [leagues, setLeagues] = useState([]);
  const [selectedLeague, setSelectedLeague] = useState(null);
  const [fixtures, setFixtures] = useState([]);
  const [filters, setFilters] = useState({ team: '', venue: '' });
  const [page, setPage] = useState(1);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    fetch('/api/leagues')
      .then(r => r.json())
      .then(d => setLeagues(d.data));
  }, []);

  useEffect(() => {
    if (!selectedLeague) return;
    fetch(`/api/fixtures/league/${selectedLeague.id}`)
      .then(r => r.json())
      .then(d => setFixtures(d.data));
  }, [selectedLeague]);

  const grouped = useMemo(() => {
    const filtered = fixtures.filter(f =>
      f.localteam.name.includes(filters.team) ||
      f.visitorteam.name.includes(filters.team)
    ).filter(f =>
      f.venue?.name?.includes(filters.venue) || true
    );
    const byDate = {};
    filtered.forEach(f => {
      const date = f.starting_at.split('T')[0];
      byDate[date] = byDate[date] || [];
      byDate[date].push(f);
    });
    return byDate;
  }, [fixtures, filters]);

  const pagedDates = Object.keys(grouped).slice(0, page * 5);

  const handleExport = () => {
    const rows = ['Date,Local vs Visitor,Result'];
    Object.entries(grouped).forEach(([dt, arr]) => {
      arr.forEach(m => rows.push(`${dt},${m.localteam.code} vs ${m.visitorteam.code},"${m.note || ''}"`));
    });
    const blob = new Blob([rows.join('\n')], { type: 'text/csv' });
    download(blob, `${selectedLeague.name}_fixtures.csv`);
  };

  return (
    <div className={darkMode ? 'series dark' : 'series'}>
      <div className="toolbar">
        <button onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? '☀️ Light' : '🌙 Dark'}
        </button>
        {!selectedLeague
          ? leagues.map(league => (
            <button key={league.id} onClick={() => setSelectedLeague(league)}>
              {league.name}
            </button>
          ))
          : <>
            <button onClick={() => setSelectedLeague(null)}>← Back to Leagues</button>
            <input placeholder="Filter team" onChange={e => setFilters(f => ({...f, team: e.target.value}))} />
            <input placeholder="Filter venue" onChange={e => setFilters(f => ({...f, venue: e.target.value}))} />
            <button onClick={handleExport}>Export CSV</button>
          </>
        }
      </div>

      <div className="list">
        {!selectedLeague ? (
          <p>Select a league to see its fixtures</p>
        ) : (
          pagedDates.map(dt => (
            <div key={dt} className="date-group">
              <h3>{dt}</h3>
              {grouped[dt].map(m => {
                const isLive = m.status === 'Live';
                const completed = m.status !== 'Live' && m.note;
                return (
                  <Link key={m.id} to={`/match/${m.id}`} className="fixture-card">
                    <img src={m.localteam.image_path} alt={m.localteam.code} className="team-logo" />
                    <div className="fixture-info">
                      <div><strong>{m.localteam.code}</strong> vs <strong>{m.visitorteam.code}</strong></div>
                      <div className="subtitle">{m.venue?.name || 'Unknown Venue'}</div>
                      {completed && <div className="result">{m.note}</div>}
                    </div>
                    <div className="status">
                      {isLive
                        ? <span className="badge live">Live</span>
                        : completed
                          ? <span className="badge completed">✓</span>
                          : <span className="badge upcoming">{m.status}</span>
                      }
                      <span className="arrow">→</span>
                    </div>
                  </Link>
                );
              })}
            </div>
          ))
        )}
      </div>

      {selectedLeague && pagedDates.length < Object.keys(grouped).length && (
        <button className="load-more" onClick={() => setPage(p => p + 1)}>Load More Dates</button>
      )}
    </div>
  );
}

function download(blob, filename) {
  const el = document.createElement('a');
  el.href = URL.createObjectURL(blob);
  el.download = filename;
  el.click();
}
