import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/TopPlayers.css';

export default function TopPlayers() {
  const [topPlayersMap, setTopPlayersMap] = useState({});
  const [playerData, setPlayerData] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const PLAYERS_JSON_URL = 'https://raw.githubusercontent.com/ashwint87/cricket-website/master/top_players.json';

  useEffect(() => {
    const fetchPlayers = async () => {
      const res = await fetch(PLAYERS_JSON_URL);
      const json = await res.json();
      const flat = Array.isArray(json?.data) ? json.data : json.data.flat();
      setTopPlayersMap(flat);
    };
    fetchPlayers();
  }, []);

  useEffect(() => {
    const loadPlayerData = async () => {
      setLoading(true);
      const results = [];

      for (const [id, name] of Object.entries(topPlayersMap)) {
        const cacheKey = `top-player-${id}`;
        const cached = sessionStorage.getItem(cacheKey);

        if (cached) {
          results.push(JSON.parse(cached));
          continue;
        }

        try {
          const res = await fetch(`/api/players/${id}`);
          const json = await res.json();
          const player = json?.data;

          if (player) {
            const playerObj = {
              id: player.id,
              name: player.fullname || name,
              image: player.image_path,
            };
            sessionStorage.setItem(cacheKey, JSON.stringify(playerObj));
            results.push(playerObj);
          }
        } catch (err) {
          console.warn(`Failed to fetch player ${id}:`, err.message);
        }
      }

      setPlayerData(results);
      setLoading(false);
    };

    if (Object.keys(topPlayersMap).length > 0) {
      loadPlayerData();
    }
  }, [topPlayersMap]);

  return (
    <div className="top-players-container">
      <div className="header">
        <h3>Top Players</h3>
        <a href="/players">
          View All
        </a>
      </div>

      {loading ? (
        <div className="top-players-loading">Loading top players...</div>
      ) : (
        <div className="top-players-grid">
          {playerData.map((player) => (
            <div
              key={player.id}
              className="top-player-card"
              onClick={() => navigate(`/player/${player.id}`)}
            >
              <img src={player.image} alt={player.name} />
              <p>{player.name}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
