import React, { useEffect, useState } from 'react';
import './Home.css';
import { Link } from 'react-router-dom';

const Home = () => {
  const [matches, setMatches] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const response = await fetch(`/api/live`);
        const json = await response.json();
        setMatches(json.data);
      } catch (error) {
        console.error('Error fetching match details', error);
      }
    };

    fetchMatches();
  }, []);

  console.log(matches);

  if (!matches || matches.length === 0) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>üèè Live Cricket Scores</h2>
        <p>No live matches at the moment.</p>
      </div>
    );
  }

  return (
    <div className="matchlist-container">
      <h2 className="matchlist-heading">üèè Live Cricket Scores</h2>

      {matches.map((match) => (
        <Link key={match.id} to={`/match/${match.id}`} state={{ from: 'home' }} className="match-card-link">
          <div className="match-card">
            <div className="match-header">
              <h3 className="match-title">{match.localteam.name} vs {match.visitorteam.name}</h3>
              {match.live && <span className="live-badge">LIVE</span>}
            </div>

            {match.scoreboards?.length > 0 &&
              match.scoreboards
                .filter(sb => sb.type === 'total')
                .map(sb => {
                  const team = match.localteam.id === sb.team_id
                    ? match.localteam
                    : match.visitorteam;

                  return (
                    <div key={sb.id} className="team-score">
                      <img
                        src={team.image_path}
                        alt={team.name}
                        className="team-flag"
                      />
                      <span className="score-line">
                        <strong>{team.name}</strong>: {sb.total}/{sb.wickets} in {sb.overs} overs
                      </span>
                    </div>
                  );
            })}

            {match.note && <p className="match-note"><em>{match.note}</em></p>}
          </div>
        </Link>
      ))}
    </div>
  );
};

export default Home;
