import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import CountdownTimer from './../CountdownTimer';
import NotificationButton from './../NotificationButton';
import './css/SeriesMatchesTab.css';

export default function SeriesMatchesTab({ seriesId }) {
  const [matches, setMatches] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`/api/series/${seriesId}/matches`).then(res => {
      setMatches(res.data.data || []);
    });
  }, [seriesId]);

  const getTeamRuns = (match, teamId) => {
    if (!match.runs || !Array.isArray(match.runs)) return '';
    const innings = match.runs.filter(r => r.team_id === teamId);
    return innings
      .map(r => `${r.score}/${r.wickets} (${r.overs} ov)`)
      .join(' & ');
  };

  const groupedByType = matches.reduce((acc, match) => {
    const type = match.type || 'Other';
    if (!acc[type]) acc[type] = [];
    acc[type].push(match);
    return acc;
  }, {});

  const liveMatchStatuses = [
    '1st Innings', '2nd Innings', '3rd Innings', '4th Innings',
    'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4',
    'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.',
  ];

  const otherMatchStatuses = [
    'Delayed',
  ];

  const completedMatchStatuses = [
    'Finished', 'Cancl', 'Aban.',
  ];

  const upcomingMatchStatuses = [
    'NS', 'Postp.',
  ];

  const liveStatuses = [...liveMatchStatuses, ...otherMatchStatuses];
  const liveMatches = matches.filter(m => liveStatuses.includes(m.status));
  const upcomingMatches = matches.filter(m => upcomingMatchStatuses.includes(m.status));
  const completedMatches = matches.filter(m => completedMatchStatuses.includes(m.status));

  return (
    <div className="series-matches-tab">
      {Object.entries(groupedByType).map(([type, typeMatches]) => (
        <div key={type}>
          <h3 className="match-type-header">{type} Matches</h3>
          <div className="match-cards-row">
            {typeMatches.map(match => (
              <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Series' } })}>
                <div style={{ marginTop: '10px' }}>
                  {match.status !== 'Finished' && <NotificationButton match={match} />}
                </div>

                {(match.status === 'NS' || match.status === 'Delayed' || match.status === 'Aban.') && (
                  <>
                    <p>
                      <img src={match.localteam.image_path} alt="" className="flag-icon" />{match.localteam.name}
                    </p>
                    <p>
                      <img src={match.visitorteam.image_path} alt="" className="flag-icon" />{match.visitorteam.name}
                    </p>
                  </>
                )}

                {getTeamRuns(match, match.localteam.id) && (
                  <p>
                    <img src={match.localteam.image_path} alt="" className="flag-icon" />
                    {match.localteam.name}: {getTeamRuns(match, match.localteam.id)}
                  </p>
                )}
                {getTeamRuns(match, match.visitorteam.id) && (
                  <p>
                    <img src={match.visitorteam.image_path} alt="" className="flag-icon" />
                    {match.visitorteam.name}: {getTeamRuns(match, match.visitorteam.id)}
                  </p>
                )}

                {match.status === 'NS' && (
                  <CountdownTimer startTime={match.starting_at} />
                )}

                <div className="match-meta">
                  <span className="venue">{match.venue?.name}, {match.venue?.city}</span>
                </div>

                <p className="status">{match.note}</p>

                {match.status === 'Finished' && match.manofmatch?.fullname && (
                  <p className="status">Man of the match: {match.manofmatch.fullname}</p>
                )}

                {match.id && !upcomingMatchStatuses.includes(match.status) && !otherMatchStatuses.includes(match.status) && (
                  <div className="notify-btn">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        navigate(`/match/${match.id}`);
                      }}
                    >
                      Scorecard
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
