import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function InfoTab({ matchId }) {
  const [info, setInfo] = useState(null);

  useEffect(() => {
    axios.get(`/api/match/${matchId}`).then(res => setInfo(res.data.data));
  }, [matchId]);

  if (!info) return <p>Loading match info...</p>;

  return (
    <div>
      <p><strong>Venue:</strong> {info?.venue?.name}, {info?.venue?.city}</p>
      <p><strong>League:</strong> {info?.league?.name}</p>
      <p><strong>Teams:</strong> {info?.localteam?.name} vs {info?.visitorteam?.name}</p>
    </div>
  );
}
