import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './css/TeamMatches.css';

export default function TeamMatches() {
  const { id } = useParams(); // teamId from route
  const [matches, setMatches] = useState([]);
  const [teamCache, setTeamCache] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await axios.get(`/api/teams/${id}/matches`);
        setMatches(res.data.data.fixtures || []);
      } catch (err) {
        console.error('Error fetching team matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [id]);

  const getTeamName = async (teamId) => {
    if (teamCache[teamId]) return teamCache[teamId];
    try {
      const res = await axios.get(`/api/teams/${teamId}`);
      const name = res.data.data.name;
      setTeamCache((prev) => {
        const updated = { ...prev, [teamId]: name };
        return updated;
      });
      return name;
    } catch {
      return 'Unknown';
    }
  };

  const [teamNames, setTeamNames] = useState({});

  useEffect(() => {
    const loadAllTeamNames = async () => {
      const ids = new Set();
      matches.forEach((match) => {
        ids.add(match.localteam_id);
        ids.add(match.visitorteam_id);
      });

      const result = {};
console.log(ids);
      for (let teamId of ids) {
console.log(await getTeamName(teamId));
        const name = await getTeamName(teamId);
console.log(name);
        result[teamId] = name;
      }

      setTeamNames(result);
    };

    if (matches.length > 0) {
      loadAllTeamNames();
    }
  }, [matches]);
console.log(teamNames);
  const classifyMatches = () => {
    const live = [];
    const upcoming = [];
    const completed = [];

    for (let m of matches) {
      if (m.live) live.push(m);
      else if (m.status === 'NS') upcoming.push(m);
      else completed.push(m);
    }

    return { live, upcoming, completed };
  };

  const { live, upcoming, completed } = classifyMatches();

  const renderMatchCard = (match) => (
console.log(teamNames[match.localteam_id]),
    <div key={match.id} className="match-card">
      <h4>{teamNames[match.localteam_id]} vs {teamNames[match.visitorteam_id]}</h4>
      <p>Status: {match.status}</p>
      <p>Start Time: {new Date(match.starting_at).toLocaleString()}</p>
    </div>
  );

  if (loading) return <p>Loading team matches...</p>;

  return (
    <div className="team-matches">
      <h2>Team Matches</h2>

      {live.length > 0 && (
        <>
          <h3>ðŸ”´ Live Matches</h3>
          <div className="match-section">{live.map(renderMatchCard)}</div>
        </>
      )}

      {upcoming.length > 0 && (
        <>
          <h3>ðŸ“… Upcoming Matches</h3>
          <div className="match-section">{upcoming.map(renderMatchCard)}</div>
        </>
      )}

      {completed.length > 0 && (
        <>
          <h3>âœ… Completed Matches</h3>
          <div className="match-section">{completed.map(renderMatchCard)}</div>
        </>
      )}
    </div>
  );
}
