import React, { useEffect, useRef } from 'react';
import './CommentaryTab.css';

const emojiForDismissal = (dismissal) => {
if (!dismissal) return '';
const text = dismissal.toLowerCase();
if (text.includes('run out')) return '🚫';
if (text.includes('lbw')) return '✋';
if (text.includes('bowled')) return '🎯';
if (text.includes('catch') || text.includes('c ')) return '🤲';
if (text.includes('retired')) return '💤';
return '🏏';
};

const formatDismissal = (text) => {
if (!text) return '';
return text
.split(' ')
.map((word) =>
word.length > 1 ? word.charAt(0).toUpperCase() + word.slice(1).toLowerCase() : word.toLowerCase()
)
.join(' ');
};

const CommentaryTab = ({ commentary = [], scorecard = [] }) => {
const scrollRef = useRef(null);

// Group commentary by innings and over
const inningsMap = {};

commentary.forEach((entry) => {
const { inning, over } = entry;
if (!inningsMap[inning]) inningsMap[inning] = {};
if (!inningsMap[inning][over]) inningsMap[inning][over] = [];
inningsMap[inning][over].push(entry);
});

// Scroll to top of latest inning
useEffect(() => {
if (scrollRef.current) {
scrollRef.current.scrollIntoView({ behavior: 'smooth' });
}
}, []);

// Sort innings reverse (latest first), overs reverse (latest over first)
const sortedInnings = Object.keys(inningsMap)
.sort((a, b) => b - a)
.map((inningKey) => ({
inning: parseInt(inningKey),
overs: Object.entries(inningsMap[inningKey])
.sort((a, b) => b[0] - a[0])
.map(([over, balls]) => ({
over: parseInt(over),
balls: balls.sort((a, b) => b.ball - a.ball),
})),
}));

// Get summary after an over
const getOverSummary = (inningIndex, overNum) => {
const inning = scorecard[inningIndex];
if (!inning) return null;

const totalRuns = inning.batting?.reduce((acc, b) => acc + (b.r || 0), 0) + (inning.extras?.r || 0);
const latestBatsmen = inning.batting
  ?.filter((b) => b.dismissal === 'not out')
  .slice(-2)
  .map((b) => `${b.batsman?.name} ${b.r}(${b.b})`);

const bowlerStats = inning.bowling?.find((b) => Math.floor(b.o) === overNum);
const bowlerSummary = bowlerStats
  ? `${bowlerStats.bowler?.name}: ${bowlerStats.o} overs, ${bowlerStats.r} runs, ${bowlerStats.w} wickets`
  : '';

return {
  totalRuns,
  batsmen: latestBatsmen,
  bowlerSummary,
};
};

return (
<div className="commentary-tab" ref={scrollRef}>
{sortedInnings.map((inningBlock, iIndex) => (
<div key={iIndex} className="inning-block">
<h3>Inning {inningBlock.inning + 1}</h3>
{inningBlock.overs.map((overBlock, oIndex) => {
const overRuns = overBlock.balls.reduce((sum, b) => sum + (b.runs || 0) + (b.extras || 0), 0);
const overProgress = overBlock.balls
.slice()
.reverse()
.map((b, idx) => (
<span
key={idx}
className={`ball ${b.runs === 0 ? 'dot' : b.runs >= 4 ? 'boundary' : 'run'}`}
>
{b.runs || (b.extras ? 'E' : '•')}
</span>
));

        const summary = getOverSummary(inningBlock.inning, overBlock.over);

        return (
          <div key={oIndex} className="over-block">
            <div className="over-number">Over {overBlock.over}</div>
            {overBlock.balls.map((entry, bIndex) => {
              const batsman = entry.batsman?.name || 'Unknown';
              const bowler = entry.bowler?.name || 'Unknown';
              const dismissal = entry.dismissal ? `${emojiForDismissal(entry.dismissal)} ${formatDismissal(entry.dismissal)}` : '';
              const isOut = !!entry.dismissal;

              return (
                <div key={bIndex} className={`commentary-entry ${isOut ? 'dismissal' : ''}`}>
                  <div className="commentary-text">
                    <strong>{bowler}</strong> to <strong>{batsman}</strong>{' '}
                    {!isOut && `- ${entry.runs} run${entry.runs !== 1 ? 's' : ''}`}
                    {!isOut && entry.extras ? ` + ${entry.extras} extra${entry.extras > 1 ? 's' : ''}` : ''}
                    {isOut && ` - ${dismissal} OUT`}
                  </div>
                </div>
              );
            })}
            <div className="over-summary">
              <div className="circle">{overRuns}</div>
              <div className="over-progress">{overProgress}</div>
              {summary && (
                <>
                  <p><strong>Batters:</strong> {summary.batsmen?.join(', ')}</p>
                  <p><strong>Bowler:</strong> {summary.bowlerSummary}</p>
                  <p><strong>Total Score:</strong> {summary.totalRuns}</p>
                </>
              )}
            </div>
          </div>
        );
      })}
    </div>
  ))}
</div>


);
};

export default CommentaryTab;
