import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './SeriesStandings.css';

export default function SeriesStandings() {
  const { id } = useParams();
  const [standings, setStandings] = useState([]);

  useEffect(() => {
    fetch(`/api/series/${id}/standings`)
      .then(res => res.json())
      .then(data => setStandings(data.data?.standings || []))
      .catch(console.error);
  }, [id]);

  return (
    <div className="series-standings">
      <h2 className="section-title">ðŸ“Š Standings</h2>
      <table className="standings-table">
        <thead>
          <tr>
            <th>Team</th>
            <th>Played</th>
            <th>Won</th>
            <th>Lost</th>
            <th>Points</th>
          </tr>
        </thead>
        <tbody>
          {standings.map(row => (
            <tr key={row.team_id}>
              <td>{row.team?.name}</td>
              <td>{row.played}</td>
              <td>{row.won}</td>
              <td>{row.lost}</td>
              <td>{row.points}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
