import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './css/Players.css';

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [query, setQuery] = useState('');
  const [offset, setOffset] = useState(0);

  const fetchPlayers = async () => {
    const res = await fetch(`/api/players?offset=${offset}&search=${encodeURIComponent(query)}`);
    const data = await res.json();
    const playerData = data?.data;

    const imageMapRes = await fetch('/api/player-image-map');
    const map = await imageMapRes.json();

    // Combine name and image
    const filteredPlayers = (name) => {
      playerData.map((name) => ({
        name,
        image: map[name] || 'https://em-content.zobj.net/source/apple/354/bust-in-silhouette_1f464.png',
      }));
    };

    setPlayers(playerData || []);
  };

  useEffect(() => {
    fetchPlayers();
  }, [offset, query]);

  return (
    <div className="players-container">
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search players..."
        value={query}
        onChange={e => {
          setQuery(e.target.value);
          setOffset(0);
        }}
      />

      <div className="player-grid">
        {players.map(p => (
          <Link key={p.id} to={`/player/${p.id}`} className="player-card">
            <h4>{p.name}</h4>

            <p>{p.country}</p>
          </Link>
        ))}
      </div>

      {!query && (
        <div className="paging">
          <button onClick={() => setOffset(o => Math.max(0, o - 50))} disabled={offset === 0}>
            Prev
          </button>
          <button onClick={() => setOffset(o => o + 50)}>Next</button>
        </div>
      )}
    </div>
  );
}
