import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../css/NewsTab.css';

const NewsTab = ({ matchId, matchInfo }) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  const info = matchInfo;
  if (!info) return <div>No match info available at the moment.</div>;

  const matchName = `${info?.localteam?.name} vs ${info?.visitorteam?.name}, ${info?.round}`;

  const toSearchQuery = (str) =>
    str
      .replace(/,/g, '')              // remove commas
      .replace(/\s+/g, '+')           // replace spaces with +
      .replace(/[^\w+]/g, '');        // remove all other special characters (optional)

  const searchQuery = toSearchQuery(matchName);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch(`/api/news?q=${searchQuery}`);
        const json = await response.json();
        setArticles(json.articles || []);
      } catch (error) {
        console.error('Error fetching news articles', error);
      } finally {
        setLoading(false);  // ✅ stop loading
      }
    };

    fetchNews();
  }, []);

  if (loading) {
    return (
      <div style={{ padding: '20px' }}>
        <p>Loading News...</p>
      </div>
    );
  }

  if (!articles || articles.length === 0) {
    return (
      <div style={{ padding: '20px' }}>
        <p>No articles at the moment.</p>
      </div>
    );
  }

  return (
    <div className="news-container">
      <div className="news-grid">
        {articles.map((article, index) => (
          <div key={index} className="news-card">
            <img
              src={article.image}
              alt={article.title}
              className="news-image"
            />
            <h2 className="news-heading">{article.title}</h2>
            <p className="news-meta">
              {article.source.name} •{' '}
              {new Date(article.publishedAt).toLocaleString()}
            </p>
            <p className="news-description">{article.description}</p>
            <a
              href={article.url}
              target="_blank"
              rel="noreferrer"
              className="news-link"
            >
              Read more →
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default NewsTab;
