import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './TeamSquad.css';

export default function TeamSquad() {
  const { id, seasonId } = useParams(); // team ID and season ID from URL
  const [players, setPlayers] = useState([]);
  const [teamName, setTeamName] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSquad = async () => {
      try {
        const res = await fetch(`/api/team/${id}/squad/${seasonId}`);
        const json = await res.json();
        setPlayers(json.data?.squad || []);
        setTeamName(json.data?.name || '');
      } catch (err) {
        console.error('Failed to fetch squad:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchSquad();
  }, [id, seasonId]);

  if (loading) return <div className="loading">Loading Squad...</div>;
  if (players.length === 0) return <div className="no-data">No squad found for this team.</div>;

  return (
    <div className="squad-container">
      <h2 className="squad-title">{teamName} Squad</h2>
      <div className="squad-grid">
        {players.map(player => (
          <div key={player.id} className="squad-card">
            <img src={player.image_path} alt={player.fullname} className="player-image" />
            <div className="player-info">
              <h4>{player.fullname}</h4>
              <p>{player.position?.name || 'Player'}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
