import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getTossWinnerName, getTossDecision, getTeamRuns, matchYear } from './../utils/matchUtils';
import {
  liveMatchStatuses,
  completedMatchStatuses,
  upcomingMatchStatuses,
  otherMatchStatuses
} from './../../constants/matchStatusConstants';
import CountdownTimer from './CountdownTimer';
import NotificationButton from './NotificationButton';
import { useSchedule } from './../context/ScheduleContext';
import './css/Schedule.css';

const Schedule = () => {
  const { matches, loading } = useSchedule();
  const [activeTab, setActiveTab] = useState('upcoming');
  const navigate = useNavigate();

  const liveStatuses = [...liveMatchStatuses, ...otherMatchStatuses];
  const liveMatches = matches.filter(m => liveStatuses.includes(m.status));
  const upcomingMatches = matches.filter(m => upcomingMatchStatuses.includes(m.status));
  const completedMatches = matches.filter(m => completedMatchStatuses.includes(m.status));

  const tabConfig = {
    upcoming: {
      label: 'ðŸŸ¡ Upcoming Matches',
      matches: upcomingMatches,
      emptyText: 'No upcoming matches',
    },
    live: {
      label: 'ðŸŸ¢ Live Matches',
      matches: liveMatches,
      emptyText: 'No live matches',
    },
    completed: {
      label: 'ðŸ”´ Completed Matches',
      matches: completedMatches,
      emptyText: 'No completed matches',
    },
  };

  const currentTab = tabConfig[activeTab];

  const matchStageName = (match) => {
    return match?.stage?.name + ', ' + matchYear(match);
  };

  const venueName = (match) => {
    return match.venue?.name && match.venue?.city
      ? `${match.venue.name}, ${match.venue.city}`
      : 'TBD';
  };

  const renderMatchCard = (match) => {
    return (
      <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`)}>
        <div className="match-header-top">
          {match.type && (
            <span className="match-type-tag">{match.type}</span>
          )}

          {upcomingMatchStatuses.includes(match.status) &&
            <NotificationButton match={match} />
          }
        </div>

        <div className="match-header">
          <p>{matchStageName(match)}</p>

          {(match.status === 'NS' || match.status === 'Delayed' || match.status === 'Aban.') && (
            <>
              <p>
                <img src={match.localteam.image_path} alt="" className="flag-icon" />{match.localteam.name}
              </p>
              <p>
                <img src={match.visitorteam.image_path} alt="" className="flag-icon" />{match.visitorteam.name}
              </p>
            </>
          )}
        </div>

        {match.status !== 'NS' && (
          <div className="match-score">
            <p>
              <img src={match.localteam.image_path} alt="" className="flag-icon" />
              {match.localteam.name} {getTeamRuns(match, match.localteam.id) && (getTeamRuns(match.localteam.id))}
            </p>
            <p>
              <img src={match.visitorteam.image_path} alt="" className="flag-icon" />
              {match.visitorteam.name} {getTeamRuns(match.visitorteam.id) && (getTeamRuns(match.visitorteam.id))}
            </p>
          </div>
        )}

        {match.status === 'NS' && !match?.elected && (
          <CountdownTimer startTime={match.starting_at} offsetMs={600000} />
        )}

        {match?.note ? (
          <p className="status">{match.note}</p>
        ) : (
          match?.toss_won_team_id && match?.elected && (
            <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>
          )
        )}

        {match.status === 'Finished' && (
          <p className="status">
            Man of the match: {match.manofmatch?.fullname || 'N/A'}
          </p>
        )}

        <div className="match-meta">
          <span className="venue">Venue: {venueName(match)}</span>
        </div>

        <div className="notify-btn">
          {match.id && !upcomingMatchStatuses.includes(match.status) && !otherMatchStatuses.includes(match.status) && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                navigate(`/match/${match.id}`);
              }}
            >
              Scorecard
            </button>
          )}

          {match?.id && match?.stage_id && match.season_id && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                navigate(`/series/${match.stage_id}/${match.season_id}`, {
                  state: { seriesLabel: matchStageName(match), seriesCode: match?.type },
                });
              }}
            >
              Series
            </button>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="schedule-container">
      <h2>Match Schedule</h2>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        <>
          <div className="tabs">
            {Object.keys(tabConfig).map((key) => (
              <button
                key={key}
                className={activeTab === key ? 'active' : ''}
                onClick={() => setActiveTab(key)}
                style={{ marginRight: '10px' }}
              >
                {tabConfig[key].label}
              </button>
            ))}
          </div>

          <div className="schedule-list" style={{ marginTop: '20px' }}>
            {currentTab.matches.length > 0
              ? currentTab.matches.map(renderMatchCard)
              : <p>{currentTab.emptyText}</p>}
          </div>
        </>
      )}
    </div>
  );
}

export default Schedule;
