import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const PlayerDetails = () => {
  const { id } = useParams();
  const [player, setPlayer] = useState(null);

  useEffect(() => {
    fetch(`/api/players/${id}`)
      .then(res => res.json())
      .then(data => setPlayer(data.data))
      .catch(console.error);
  }, [id]);

  if (!player) return <p>Loading player...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>{player.fullname}</h2>
      <img src={player.image_path} alt={player.fullname} style={{ width: '150px', borderRadius: '10px' }} />
      <p><strong>Country ID:</strong> {player.country_id}</p>
      <p><strong>Batting Style:</strong> {player.battingstyle}</p>
      <p><strong>Bowling Style:</strong> {player.bowlingstyle}</p>

      <h3>Career Stats</h3>
      {player.career?.length > 0 ? (
        player.career.map((c, i) => (
          <div key={i} style={{ marginBottom: '10px' }}>
            <strong>{c.type}</strong>: {c.batting?.matches} matches, {c.batting?.runs_scored} runs, avg: {c.batting?.average}
          </div>
        ))
      ) : (
        <p>No career data available.</p>
      )}
    </div>
  );
};

export default PlayerDetails;
