import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const PlayersList = () => {
  const [players, setPlayers] = useState([]);
  const [offset, setOffset] = useState(0);
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetch(`/api/players?offset=${offset}`)
      .then(res => res.json())
      .then(json => setPlayers(json.data || []));
  }, [offset]);

  const filteredPlayers = players.filter(p =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="players-list">
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search by name"
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <div className="players-grid">
        {filteredPlayers.map(p => (
          <Link key={p.id} to={`/players/${p.id}`} className="player-card">
            <img src={p.image} alt={p.name} />
            <h4>{p.name}</h4>
            <p>{p.country}</p>
          </Link>
        ))}
      </div>
      <div className="pagination">
        <button onClick={() => setOffset(Math.max(0, offset - 30))} disabled={offset === 0}>
          Previous
        </button>
        <button onClick={() => setOffset(offset + 30)}>Next</button>
      </div>
    </div>
  );
};

export default PlayersList;
