import React from 'react';
import './LineupTab.css';

const LineupsTab = ({ match }) => {
  const lineup = match.lineup || [];

  if (!lineup.length) {
    return <div>Lineups not available.</div>;
  }

  const localTeam = match.localteam || {};
  const visitorTeam = match.visitorteam || {};

  const localTeamPlayers = lineup.filter(player => player.team_id === localTeam.id);
  const visitorTeamPlayers = lineup.filter(player => player.team_id === visitorTeam.id);

  const renderPlayers = (players) => (
    <ul>
      {players.map((player) => (
        <li key={player.id}>
          {player.fullname || player.firstname + ' ' + player.lastname}
          {player.position?.name ? ` - ${player.position.name}` : ''}
          {player.captain && ' (C)'}
          {player.wk && ' (WK)'}
        </li>
      ))}
    </ul>
  );

  return (
    <div className="lineups-tab">
      <h3>{localTeam.name} Playing XI</h3>
      {renderPlayers(localTeamPlayers)}

      <h3>{visitorTeam.name} Playing XI</h3>
      {renderPlayers(visitorTeamPlayers)}
    </div>
  );
};

export default LineupsTab;
