import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary }) => {
  if (!commentary || commentary.length === 0) return <p>No commentary available.</p>;

  // Group by innings
  const inningsGroups = {};
  commentary.forEach(ball => {
    const inning = ball.inning;
    if (!inningsGroups[inning]) inningsGroups[inning] = [];
    inningsGroups[inning].push(ball);
  });

  // Function to generate partnerships per innings
  const getPartnerships = (balls) => {
    const partnerships = [];
    let striker = null;
    let nonStriker = null;
    let runs = 0;
    let ballsFaced = 0;
    let extras = 0;

    for (let i = 0; i < balls.length; i++) {
      const ball = balls[i];
      const batsman = ball.batsman?.name || 'Unknown';
      const isWicket = !!ball.dismissal;
      const ballRuns = ball.runs || 0;
      const ballExtras = ball.extras || 0;

      // Init partnership with first batsman
      if (!striker) {
        striker = batsman;
      } else if (!nonStriker && batsman !== striker) {
        nonStriker = batsman;
      }

      runs += ballRuns + ballExtras;
      ballsFaced += 1;
      extras += ballExtras;

      if (isWicket) {
        const pair = striker && nonStriker ? `${striker} & ${nonStriker}` : striker || nonStriker || batsman;
        partnerships.push({ pair, runs, balls: ballsFaced, extras });

        // Now update for next partnership
        const nextBall = balls[i + 1];
        const nextBatsman = nextBall?.batsman?.name;

        // One of the batsmen is still there – keep the other
        if (nextBatsman && (nextBatsman !== striker && nextBatsman !== nonStriker)) {
          // Assume striker is out
          striker = nextBatsman;
        } else {
          striker = nextBatsman || null;
        }

        // Keep non-striker if possible
        if (striker === nonStriker) {
          nonStriker = null;
        }

        runs = 0;
        ballsFaced = 0;
        extras = 0;
      }
    }

    return partnerships;
  };

  return (
    <div className="partnership-tab">
      {Object.keys(inningsGroups)
        .sort((a, b) => b - a)
        .map((inningNum) => {
          const balls = inningsGroups[inningNum];
          const partnerships = getPartnerships(balls);

          return (
            <div key={inningNum} className="inning-section">
              <h3>Inning {parseInt(inningNum) + 1}</h3>
              {partnerships.length === 0 ? (
                <p>No partnerships recorded.</p>
              ) : (
                <table className="partnership-table">
                  <thead>
                    <tr>
                      <th>Batting Pair</th>
                      <th>Runs</th>
                      <th>Balls</th>
                      <th>Extras</th>
                    </tr>
                  </thead>
                  <tbody>
                    {partnerships.map((p, idx) => (
                      <tr key={idx}>
                        <td>{p.pair}</td>
                        <td>{p.runs}</td>
                        <td>{p.balls}</td>
                        <td>{p.extras}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          );
        })}
    </div>
  );
};

export default PartnershipTab;
