import React, { useState, useEffect } from 'react';
import './TeamRankings.css';

const TeamRankings = () => {
  const [rankings, setRankings] = useState([]);
  const [format, setFormat] = useState('odi');

  useEffect(() => {
    fetch('/api/team-rankings')
      .then((res) => res.json())
      .then((data) => setRankings(data))
      .catch((err) => console.error('Failed to load rankings:', err));
  }, []);

  const formatNames = {
    test: 'Test',
    odi: 'ODI',
    t20i: 'T20I'
  };

  const filteredRankings = rankings.filter((r) => r.type === format);

  return (
    <div className="rankings-container">
      <h2>Team Rankings</h2>
      <div className="ranking-tabs">
        {Object.keys(formatNames).map((key) => (
          <button
            key={key}
            className={format === key ? 'active' : ''}
            onClick={() => setFormat(key)}
          >
            {formatNames[key]}
          </button>
        ))}
      </div>

      <table className="ranking-table">
        <thead>
          <tr>
            <th>Pos</th>
            <th>Team</th>
            <th>Matches</th>
            <th>Points</th>
            <th>Rating</th>
          </tr>
        </thead>
        <tbody>
          {filteredRankings.map((team, index) => (
            <tr key={team.team_id}>
              <td>{team.position}</td>
              <td className="team-cell">
                <img
                  src={team.team?.image_path}
                  alt={team.team?.code}
                  className="team-flag"
                />
                <span>{team.team?.name}</span>
              </td>
              <td>{team.matches}</td>
              <td>{team.points}</td>
              <td>{team.rating}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TeamRankings;
