import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Section({ title, children }) {
  return (
    <div className="p-4 border-b border-gray-300">
      <h2 className="text-xl font-bold mb-2">{title}</h2>
      {children}
    </div>
  );
}

function LiveScores() {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/live')
      .then(res => setMatches(res.data.data || []));
  }, []);

  return (
    <Section title="ðŸ Live Scores">
      {matches.map(match => (
        <div key={match.id} className="mb-2">
          <strong>{match.localteam?.name}</strong> vs <strong>{match.visitorteam?.name}</strong> â€” {match.status}
        </div>
      ))}
    </Section>
  );
}

function Fixtures() {
  const [fixtures, setFixtures] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/fixtures')
      .then(res => setFixtures(res.data.data || []));
  }, []);

  return (
    <Section title="ðŸ“… Upcoming Fixtures">
      {fixtures.map(fix => (
        <div key={fix.id} className="mb-2">
          {fix.localteam?.name} vs {fix.visitorteam?.name} â€” {new Date(fix.starting_at).toLocaleString()}
        </div>
      ))}
    </Section>
  );
}

function Leagues() {
  const [leagues, setLeagues] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/leagues')
      .then(res => setLeagues(res.data.data || []));
  }, []);

  return (
    <Section title="ðŸŒ Leagues">
      {leagues.map(league => (
        <div key={league.id} className="mb-1">
          {league.name}
        </div>
      ))}
    </Section>
  );
}

function Teams() {
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/teams')
      .then(res => setTeams(res.data.data || []));
  }, []);

  return (
    <Section title="ðŸŸï¸ Teams">
      {teams.map(team => (
        <div key={team.id} className="mb-1">
          {team.name}
        </div>
      ))}
    </Section>
  );
}

function App() {
  return (
    <div className="max-w-3xl mx-auto p-6 bg-gray-100 min-h-screen font-sans">
      <h1 className="text-3xl font-bold text-center mb-6">ðŸ“Š Cricket Dashboard</h1>
      <LiveScores />
      <Fixtures />
      <Leagues />
      <Teams />
    </div>
  );
}

export default App;
