import React, { useEffect, useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5050/api/teams')
      .then((res) => res.json())
      .then((data) => {
        setTeams(data.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching teams:', err);
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="teams-page">Loading teams...</div>;

  return (
    <div className="teams-page">
      <h2>All Teams</h2>
      <div className="teams-grid">
        {teams.map((team) => (
          <div key={team.id} className="team-card">
            <img
              src={team.image_path}
              alt={team.code}
              className="team-logo"
            />
            <span className="team-name">{team.name}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Teams;
