import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import CountdownTimer from './../CountdownTimer';

export default function SeriesMatchesTab({ seriesId }) {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    axios.get(`/api/series/${seriesId}/matches`).then(res => {
      setMatches(res.data.data || []);
    });
  }, [seriesId]);

  return (
    <div>
      {matches.map(match => (
        <div key={match.id} style={{ marginBottom: '10px' }}>
          <Link to={`/match/${match.id}`}>
            <strong>{match.localteam?.name} vs {match.visitorteam?.name}</strong>
          </Link>
          <CountdownTimer startTime={match.starting_at} />
        </div>
      ))}
    </div>
  );
}
