import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './css/TeamMatches.css';
import axios from 'axios';

export default function TeamMatches() {
  const { id } = useParams();
  const [fixtures, setFixtures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [teamName, setTeamName] = useState('');
  const [teamCache, setTeamCache] = useState({});
  const [activeTab, setActiveTab] = useState('upcoming');

  useEffect(() => {
    const fetchFixtures = async () => {
      try {
        const res = await axios.get(`/api/teams/${id}/matches`);
        const team = res.data.data;
        setTeamName(team.name);

        const fixtures = team.fixtures || [];
        setFixtures(fixtures);
      } catch (err) {
        console.error('Error fetching team fixtures:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchFixtures();
  }, [id]);

  const getTeamName = async (teamId) => {
    if (teamCache[teamId]) return teamCache[teamId];

    try {
      const res = await axios.get(`/api/teams/${teamId}`);
console.log(res.data.data.data);
      const name = res.data.data.name;
      setTeamCache(name);
//      setTeamCache((prev) => ({ ...prev, [teamId]: name }));
//      return name;
    } catch {
      return 'Unknown';
    }
  };

  const getMatchStatus = (fixture) => {
    if (fixture.status === 'NS') return 'upcoming';
    if (fixture.status === 'Finished' || fixture.status === 'Completed') return 'completed';
    return 'live';
  };

  const renderMatchCard = (fixture, idx) => {
    const [localteamName, visitorteamName] = [
      teamCache[fixture.localteam_id],
      teamCache[fixture.visitorteam_id],
    ];

    return (
      <div className="match-card" key={idx}>
        <h4>
          {localteamName || fixture.localteam_id} vs {visitorteamName || fixture.visitorteam_id}
        </h4>
        <p>Status: {fixture.status}</p>
        <p>Type: {fixture.type}</p>
        <p>Starts at: {new Date(fixture.starting_at).toLocaleString()}</p>
      </div>
    );
  };

  useEffect(() => {
    // Resolve all unique team IDs in the background
    const resolveAllTeams = async () => {
      const teamIds = new Set();
      fixtures.forEach(f => {
        teamIds.add(f.localteam_id);
        teamIds.add(f.visitorteam_id);
      });

      await Promise.all([...teamIds].map(id => getTeamName(id)));
    };

    if (fixtures.length > 0) {
      resolveAllTeams();
    }
  }, [fixtures]);

  const filteredFixtures = fixtures.filter(f => getMatchStatus(f) === activeTab);

  return (
    <div className="team-matches-page">
      <h2>Matches for {teamName}</h2>

      <div className="tabs">
        <button className={activeTab === 'upcoming' ? 'active' : ''} onClick={() => setActiveTab('upcoming')}>Upcoming</button>
        <button className={activeTab === 'live' ? 'active' : ''} onClick={() => setActiveTab('live')}>Live</button>
        <button className={activeTab === 'completed' ? 'active' : ''} onClick={() => setActiveTab('completed')}>Completed</button>
      </div>

      {loading ? <p>Loading matches...</p> : (
        <div className="matches-grid">
          {filteredFixtures.map(renderMatchCard)}
        </div>
      )}
    </div>
  );
}
