import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary, scorecard }) => {
  if (!commentary || !scorecard) return <div>Loading...</div>;

  const groupBallsByInning = () => {
    const inningsMap = {};
    commentary.forEach(ball => {
      const inning = ball.inning || 1;
      if (!inningsMap[inning]) inningsMap[inning] = [];
      inningsMap[inning].push(ball);
    });
    return inningsMap;
  };

  const isWicketBall = (ball) => {
    if (!ball.score) return false;
    const name = ball.score.name.toLowerCase();
    return (
      name.includes('out') &&
      !name.includes('retired') &&
      !name.includes('absent') &&
      !name.includes('substitute')
    );
  };

  const renderPartnerships = (balls) => {
    const partnerships = [];
    let current = { batsmen: new Set(), runs: 0, balls: 0 };

    balls.forEach(ball => {
      const batsman = ball.batsman?.fullname;
      if (batsman) current.batsmen.add(batsman);
      if (ball.score?.runs !== undefined) current.runs += ball.score.runs;
      current.balls += 1;

      if (isWicketBall(ball)) {
        partnerships.push({
          batsmen: Array.from(current.batsmen),
          runs: current.runs,
          balls: current.balls,
          dismissal: ball.score.name,
        });
        current = { batsmen: new Set(), runs: 0, balls: 0 };
      }
    });

    // Final stand
    if (current.batsmen.size > 0) {
      partnerships.push({
        batsmen: Array.from(current.batsmen),
        runs: current.runs,
        balls: current.balls,
        dismissal: null,
      });
    }

    return partnerships;
  };

  const inningsData = groupBallsByInning();

  return (
    <div className="partnership-tab">
      {Object.entries(inningsData).map(([inning, balls]) => {
        const team = scorecard?.find(s => s.scoreboard?.endsWith(inning));
        const partnerships = renderPartnerships(balls);

        return (
          <div key={inning} className="inning-block">
            <h4>Inning {inning} - {team?.team?.name || 'Team'}</h4>
            <table>
              <thead>
                <tr>
                  <th>Batsmen</th>
                  <th>Runs</th>
                  <th>Balls</th>
                  <th>Dismissal</th>
                </tr>
              </thead>
              <tbody>
                {partnerships.map((p, i) => (
                  <tr key={i}>
                    <td>{p.batsmen.join(' & ')}</td>
                    <td>{p.runs}</td>
                    <td>{p.balls}</td>
                    <td>{p.dismissal || '-'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      })}
    </div>
  );
};

export default PartnershipTab;
