import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

const tabs = ['Scorecard', 'Commentary', 'Match Info', 'Lineups'];

function MatchDetails() {
  const { id } = useParams();
  const [match, setMatch] = useState(null);
  const [activeTab, setActiveTab] = useState('Scorecard');

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const res = await axios.get(`/api/match/${id}`); // hits your backend route
        setMatch(res.data.data);
      } catch (error) {
        console.error('Failed to fetch match details', error);
      }
    };
    fetchMatchDetails();
  }, [id]);

  if (!match) return <div>Loading match details...</div>;

  const { localteam, visitorteam, scoreboards, note, status, toss_won_team_id, elected, lineup, commentaries } = match;

  return (
    <div style={{ padding: '20px' }}>
      <Link to="/">â¬… Back</Link>
      <h1>{localteam.name} vs {visitorteam.name}</h1>
      <p>Status: <strong>{status}</strong></p>
      <p><em>{note}</em></p>

      {/* Tab headers */}
      <div style={{ display: 'flex', gap: '20px', marginTop: '20px' }}>
        {tabs.map(tab => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            style={{
              padding: '10px',
              fontWeight: activeTab === tab ? 'bold' : 'normal',
              borderBottom: activeTab === tab ? '2px solid black' : 'none',
              background: 'none',
              border: 'none',
              cursor: 'pointer'
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Tab content */}
      <div style={{ marginTop: '20px' }}>
        {activeTab === 'Scorecard' && (
          <>
            {scoreboards.map(sb => (
              <div key={sb.id} style={{ marginBottom: '10px' }}>
                <strong>{sb.team_id === localteam.id ? localteam.name : visitorteam.name}</strong>
                <div>{sb.total}/{sb.wickets} in {sb.overs} overs</div>
              </div>
            ))}
          </>
        )}

        {activeTab === 'Commentary' && (
          <>
            {commentaries && commentaries.length > 0 ? (
              commentaries.map((c, idx) => (
                <div key={idx} style={{ marginBottom: '5px' }}>
                  <strong>{c.over}.{c.ball}:</strong> {c.comment}
                </div>
              ))
            ) : (
              <div>No commentary available.</div>
            )}
          </>
        )}

        {activeTab === 'Match Info' && (
          <div>
            <p><strong>Toss:</strong> {match.toss_won_team_id === localteam.id ? localteam.name : visitorteam.name} won the toss and elected to {elected}</p>
            <p><strong>Start Time:</strong> {new Date(match.starting_at).toLocaleString()}</p>
            <p><strong>Match Type:</strong> {match.type}</p>
            <p><strong>Venue ID:</strong> {match.venue_id}</p>
          </div>
        )}

        {activeTab === 'Lineups' && (
          <>
            <div>
              <strong>{localteam.name} Lineup:</strong>
              <ul>
                {lineup
                  .filter(p => p.team_id === localteam.id)
                  .map(player => (
                    <li key={player.id}>{player.fullname}</li>
                  ))}
              </ul>
            </div>
            <div>
              <strong>{visitorteam.name} Lineup:</strong>
              <ul>
                {lineup
                  .filter(p => p.team_id === visitorteam.id)
                  .map(player => (
                    <li key={player.id}>{player.fullname}</li>
                  ))}
              </ul>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default MatchDetails;
